parameters.functionCode:
const now = new Date();
const leadMinutes = parseInt($node['Set: Config'].json['LEAD_MINUTES']);
const companyDomain = $node['Set: Config'].json['COMPANY_DOMAIN'];
const fileritems = [];

// Process each calendar event
for (const item of items) {
  const eventData = item.json;

  // Skip if already started
  if (!eventData.attendees || !eventData.start || !eventData.start.dateTime) {
    continue;
  }

  const startTime = new Date(eventData.start.dateTime);
  const timeDiff = (startTime - now) / (1000 * 60); // minutes

  // Only process events starting within lead time

  if (timeDiff > 0 && timeDiff <= leadMinutes) {

    // Extract links from description
    const description = eventData.description || '';
    const cvMatch = description.match(/CV:\s*(https?:\/\/[^\s\n]+)/i);
    const notesMatch = description.match(/Notes:\s*(https?:\/\/[^\s\n]+)/i);
    const meetingLinkMatch = description.match(/(https?:\/\/[^\s\n]*(?:meet\.google\.com|zoom\.us|teams\.microsoft\.com)[^\s\n]*)/i) || 
                           eventData.hangoutLink ? [eventData.hangoutLink] : null;

    // Process each attendee

    for (const attendee of eventData.attendees) {
      // Skip organizer, declined attendees, and external emails
      if (attendee.organizer || 
          attendee.responseStatus === 'declined' ||
          !attendee.email) {
        continue;
      }

      // Create item for each valid attendee
      fileritems.push({
        eventId: eventData.id,
        eventTitle: eventData.summary || 'Interview',
        startTime: eventData.start.dateTime,
        attendeeEmail: attendee.email,
        attendeeName: attendee.displayName || attendee.email.split('@')[0],
        cvLink: cvMatch ? cvMatch[1] : null,
        notesLink: notesMatch ? notesMatch[1] : null,
        meetingLink: meetingLinkMatch ? meetingLinkMatch[0] : eventData.htmlLink,
        localStartTime: new Date(eventData.start.dateTime).toLocaleString('en-IN', {
          timeZone: $node['Set: Config'].json['TIMEZONE'],
          weekday: 'short',
          month: 'short',
          day: 'numeric',
          hour: '2-digit',
          minute: '2-digit'
        }),
        uniqueKey: `${eventData.id}_${attendee.email}`
      });
    }
  }
}

return fileritems;

const uniqueKey = $json.uniqueKey;
const sentPings = this.getWorkflowStaticData('global').sentPings || {};

// Check if already sent
const alreadySent = sentPings[uniqueKey] || false;

return {
  json: {
    ...$json,
    alreadySent: alreadySent
  }
};

