parameters.functionCode:
// Record that ping was sent
const uniqueKey = $json.uniqueKey;
const staticData = this.getWorkflowStaticData('global');

if (!staticData.sentPings) {
  staticData.sentPings = {};
}

staticData.sentPings[uniqueKey] = {
  sentAt: new Date().toISOString(),
  eventId: $json.eventId,
  attendeeEmail: $json.attendeeEmail
};

return {
  json: {
    ...$json,
    recorded: true
  }
};
