// Collect results for each URL
const output = [];

for (const item of items) {
  const d = item.json;
  const issues = [];
  const suggestions = {};

  function addIssue(code, message, suggestion) {
    issues.push({ code, message });
    suggestions[code] = suggestion;
  }

  // 1. Title length
  if (!d.title) {
    addIssue('missing_title', 'Missing <title>', 'Add a descriptive <title> (30‚Äì60 chars).');
  } else if (d.title.length < 30) {
    addIssue('short_title', `Title too short (${d.title.length} chars)`, 'Increase title length to ‚â•30 chars.');
  } else if (d.title.length > 60) {
    addIssue('long_title', `Title too long (${d.title.length} chars)`, 'Reduce title to ‚â§60 chars.');
  }

  // 2. Meta description
  if (!d.metaDesc) {
    addIssue('missing_meta_desc', 'Missing meta description', 'Add <meta name="description"> (50‚Äì160 chars).');
  } else if (d.metaDesc.length < 50) {
    addIssue('short_meta_desc', `Meta description too short (${d.metaDesc.length} chars)`, 'Increase description to ‚â•50 chars.');
  } else if (d.metaDesc.length > 160) {
    addIssue('long_meta_desc', `Meta description too long (${d.metaDesc.length} chars)`, 'Shorten description to ‚â§160 chars.');
  }

  // 3. Robots, viewport, canonical, lang
  if (!d.metaRobots) {
    addIssue('missing_robots', 'Missing meta robots tag', 'Add <meta name="robots" content="index,follow">.');
  }
  if (!d.metaViewport || !d.metaViewport.includes('width=device-width')) {
    addIssue('viewport', 'Missing/incorrect viewport tag', 'Add <meta name="viewport" content="width=device-width, initial-scale=1">.');
  }
  if (!d.linkCanonical) {
    addIssue('canonical', 'Missing canonical', 'Add <link rel="canonical" href="YOUR_URL">.');
  }
  if (!d.htmlLang) {
    addIssue('lang', 'Missing lang attribute', 'Add <html lang="en">.');
  }

  // 4. Word count
  if (typeof d.wordCount === 'number' && d.wordCount < 300) {
    addIssue('low_word_count', `Low word count (${d.wordCount} words)`, 'Aim for ‚â•300 words.');
  }

  // 5. Headings
  const h1 = (d.h1Tags || []).length;
  const h2 = (d.h2Tags || []).length;
  const h3 = (d.h3Tags || []).length;
  if (h1 !== 1) {
    addIssue('h1', `${h1} <h1> tags`, 'Use exactly one <h1>.');
  }
  if (h2 === 0) {
    addIssue('h2', 'No <h2> tags', 'Add ‚â•1 <h2> for sections.');
  }
  if (h3 === 0) {
    addIssue('h3', 'No <h3> tags', 'Use <h3> for subsections.');
  }

  // 6. Images alt text
  if (Array.isArray(d.imgAlts)) {
    const missing = d.imgAlts.filter(a => !a || !a.trim()).length;
    if (missing) {
      addIssue('img_alt', `${missing} images missing alt`, 'Provide descriptive alt text.');
    }
  }

  // 7. Broken links
  if (Array.isArray(d.links)) {
    const broken = d.links.filter(l => l.status >= 400).length;
    if (broken) {
      addIssue('broken_links', `${broken} broken links`, 'Fix or remove broken URLs.');
    }
  }

  // 8. Structured data
  if (!d.structuredData || d.structuredData.length === 0) {
    addIssue('schema', 'No structured data', 'Implement JSON-LD schema markup.');
  }

  // 9. Core Web Vitals
  if (d.metrics) {
    const { LCP, INP, CLS } = d.metrics;
    if (LCP > 2500) {
      addIssue('lcp', `LCP too slow (${LCP} ms)`, 'Optimize images and server response.');
    }
    if (INP > 200) {
      addIssue('inp', `INP high (${INP} ms)`, 'Minimize JavaScript execution.');
    }
    if (CLS > 0.1) {
      addIssue('cls', `CLS unstable (${CLS})`, 'Set image dimensions and pre-load fonts.');
    }
  }

  // Summary
  const pass = issues.length === 0;
  const summary = pass ? 'All checks passed! üéâ' : `${issues.length} issue${issues.length > 1 ? 's' : ''} detected`;

  // Markdown report
  let markdown = `## SEO Audit for ${d.siteUrl || d.url}\n`;
  markdown += `**Status:** ${pass ? 'PASS ‚úÖ' : 'FAIL ‚ùå'}\n`;
  markdown += `**Summary:** ${summary}\n`;
  if (!pass) {
    markdown += `\n### Issues & Suggestions\n`;
    issues.forEach(({ code, message }) => {
      markdown += `- **${message}**  \n  _Suggestion:_ ${suggestions[code]}\n`;
    });
  }

  output.push({
    json: {
      siteUrl: d.siteUrl || d.url,
      pass,
      summary,
      issues,
      suggestions,
      markdownReport: markdown,
    },
  });
}

return output;

