=<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8" />
  <style>
    body { font-family: Arial, sans-serif; color: #333; line-height: 1.4; padding: 20px; }
    .header { display: flex; align-items: center; margin-bottom: 20px; }
    .logo { height: 40px; margin-right: 15px; }
    .title { font-size: 24px; color: #2a6ebb; margin: 0; }
    .meta { margin: 5px 0 20px; }
    .status-pass { color: green; font-weight: bold; }
    .status-fail { color: red; font-weight: bold; }
    .summary-table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
    .summary-table td { padding: 8px; border: 1px solid #eee; }
    .issues-container { display: flex; gap: 20px; }
    .column { flex: 1; }
    .column h4 { border-bottom: 1px solid #ddd; padding-bottom: 5px; }
    .issues-list { list-style: none; padding: 0; margin: 0; }
    .issues-list li { margin: 8px 0; }
    .suggestion { display: block; margin-left: 15px; font-style: italic; color: #555; }
    .priorities { background: #f9f9f9; padding: 15px; border-left: 4px solid #2a6ebb; margin-bottom: 20px; }
    .next-steps { margin-top: 30px; font-size: 14px; }
    .next-steps a { color: #2a6ebb; text-decoration: none; }
    code { background: #eef; padding: 2px 4px; border-radius: 3px; }
  </style>
</head>
<body>

  <div class="header">
    <div>
      <h1 class="title">SEO Audit Report</h1>
      <div class="meta">Date: {{ new Date().toLocaleDateString() }}</div>
    </div>
  </div>

  <p>
    <strong>Page:</strong> {{ $node["Run SEO Audit Logic"].json.siteUrl }}<br/>
    <strong>Status:</strong>
    <span class="{{ $node["Run SEO Audit Logic"].json.pass ? 'status-pass' : 'status-fail' }}">
      {{ $node["Run SEO Audit Logic"].json.pass ? 'PASS ‚úÖ' : 'FAIL ‚ùå' }}
    </span>
  </p>

  <div class="priorities">
    <strong>üö© Top 3 Priorities:</strong>
    <ol>
      {{
        $node["Run SEO Audit Logic"].json.issues
          .slice(0, 3)
          .map(i => `<li>${i.message.replace(/</g,'&lt;').replace(/>/g,'&gt;')}</li>`)
          .join('')
      }}
    </ol>
  </div>

  <table class="summary-table">
    <tr>
      <td><strong>Total Issues</strong></td>
      <td>{{ $node["Run SEO Audit Logic"].json.issues.length }}</td>
    </tr>
    <tr>
      <td><strong>Broken Links</strong></td>
      <td>
        {{
          $node["Run SEO Audit Logic"].json.issues.filter(i => i.code === 'broken_links').length
        }}
      </td>
    </tr>
    <tr>
      <td><strong>CWV Alerts</strong></td>
      <td>
        {{
          $node["Run SEO Audit Logic"].json.issues
            .filter(i => ['lcp','inp','cls'].includes(i.code))
            .length
        }}
      </td>
    </tr>
  </table>

  <div class="issues-container">
    <!-- On‚ÄëPage Issues -->
    <div class="column">
      <h4>üìù On‚ÄëPage Issues</h4>
      <ul class="issues-list">
        {{
          $node["Run SEO Audit Logic"].json.issues
            .filter(i => [
              'missing_title','short_title','long_title',
              'missing_meta_desc','short_meta_desc','long_meta_desc',
              'h1','h2','h3','img_alt','schema'
            ].includes(i.code))
            .map(i => {
              const msg = i.message.replace(/</g,'&lt;').replace(/>/g,'&gt;');
              const sug = $node["Run SEO Audit Logic"].json.suggestions[i.code]
                            .replace(/</g,'&lt;').replace(/>/g,'&gt;');
              return `
                <li>
                  ‚ö†Ô∏è <strong>${msg}</strong>
                  <span class="suggestion">
                    Suggestion: <code>${sug}</code>
                  </span>
                </li>`;
            })
            .join('')
        }}
      </ul>
    </div>

    <!-- Technical Issues -->
    <div class="column">
      <h4>üîß Technical Issues</h4>
      <ul class="issues-list">
        {{
          $node["Run SEO Audit Logic"].json.issues
            .filter(i => ![
              'missing_title','short_title','long_title',
              'missing_meta_desc','short_meta_desc','long_meta_desc',
              'h1','h2','h3','img_alt','schema'
            ].includes(i.code))
            .map(i => {
              const msg = i.message.replace(/</g,'&lt;').replace(/>/g,'&gt;');
              const sug = $node["Run SEO Audit Logic"].json.suggestions[i.code]
                            .replace(/</g,'&lt;').replace(/>/g,'&gt;');
              return `
                <li>
                  ‚öôÔ∏è <strong>${msg}</strong>
                  <span class="suggestion">
                    Suggestion: <code>${sug}</code>
                  </span>
                </li>`;
            })
            .join('')
        }}
      </ul>
    </div>
  </div>

  <div class="next-steps">
    <p><strong>Next Steps:</strong></p>
    <ol>
      <li>Review the above suggestions and assign tickets in your tracker.</li>
      <li>Re‚Äërun this audit after fixes are deployed to confirm all PASS.</li>
    </ol>
  </div>

</body>
</html>
