const token = 'ZZZZZZZZZZZZZZZZZZZZ'; //Replace with your token from eventbrite
const orgId = '1111111111111'; // Replace with your actual organization ID

const fetch = async (url) => {
  return await this.helpers.request({
    method: 'GET',
    url,
    json: true,
  });
};

const allAttendees = [];
let eventContinuation = null;
let eventHasMore = false;

// 1. Fetch all events (live + ended)
do {
  const eventUrl = eventContinuation
    ? `https://www.eventbriteapi.com/v3/organizations/${orgId}/events/?status=live,ended&continuation=${eventContinuation}&token=${token}`
    : `https://www.eventbriteapi.com/v3/organizations/${orgId}/events/?status=live,ended&token=${token}`;

  const eventData = await fetch(eventUrl);

  const events = eventData.events || [];

  for (const event of events) {
    const eventId = event.id;
    let continuation = null;
    let hasMore = false;

    do {
      const attendeeUrl = continuation
        ? `https://www.eventbriteapi.com/v3/events/${eventId}/attendees/?continuation=${continuation}&token=${token}`
        : `https://www.eventbriteapi.com/v3/events/${eventId}/attendees/?token=${token}`;

      const attendeeData = await fetch(attendeeUrl);

      for (const a of attendeeData.attendees || []) {
        let customAnswers = {};
        if (Array.isArray(a.answers)) {
          a.answers.forEach(ans => {
            customAnswers[ans.question] = ans.answer || '';
          });
        }
// Replace below with the fields you want to bring into pipedrive from eventbrite
allAttendees.push({
  json: {
    event_id: eventId,
    event_name: event.name?.text || '',
    event_date: event.start?.local || '',  // Add this line
    name: a.profile?.name || '',
    email: a.profile?.email || '',
    phone: a.profile?.phone || '',
    work_phone: a.profile?.work_phone || '',
    ticket_class: a.ticket_class_name || '',
    status: a.status,
    order_id: a.order_id,
    created: a.created
  }
});

      }

      continuation = attendeeData.pagination?.continuation || null;
      hasMore = attendeeData.pagination?.has_more_items === true;

    } while (hasMore && continuation);
  }

  eventContinuation = eventData.pagination?.continuation || null;
  eventHasMore = eventData.pagination?.has_more_items === true;

} while (eventHasMore && eventContinuation);

return allAttendees;
