parameters.functionScript:
const userQuery = items[0].json.body.query.toLowerCase();
const entities = {};

// URL extraction
const urlMatch = userQuery.match(/https?://[^\s]+/);
if (urlMatch) entities.url = urlMatch[0];

// Keyword extraction
const keywordMatch = userQuery.match(/keyword\s['"]?([\w\s]+)['"]?/);
if (keywordMatch) entities.keyword = keywordMatch[1].trim();

// Specific SEO terms
if (userQuery.includes('title')) entities.seo_topic = 'title';
if (userQuery.includes('description')) entities.seo_topic = 'meta_description';
if (userQuery.includes('h1')) entities.seo_topic = 'h1';
if (userQuery.includes('alt text')) entities.seo_topic = 'alt_text';
if (userQuery.includes('canonical')) entities.seo_topic = 'canonical';
if (userQuery.includes('noindex')) entities.seo_topic = 'noindex';
if (userQuery.includes('schema')) entities.seo_topic = 'schema';

// Technical SEO terms
if (userQuery.includes('robots.txt')) entities.technical_topic = 'robots.txt';
if (userQuery.includes('sitemap')) entities.technical_topic = 'sitemap.xml';
if (userQuery.includes('https') || userQuery.includes('ssl')) entities.technical_topic = 'https';

// Performance terms
if (userQuery.includes('lcp')) entities.performance_metric = 'lcp';
if (userQuery.includes('inp')) entities.performance_metric = 'inp';
if (userQuery.includes('core web vitals')) entities.performance_metric = 'core_web_vitals';

// If a URL is explicitly mentioned and the query asks for audit, trigger full audit
if (entities.url && (userQuery.includes('audit') || userQuery.includes('check seo')))
  entities.trigger_full_audit = true;

return [{ json: { ...items[0].json, entities: entities } }];
