// Process scraped backlink data and analyze opportunities
const inputData = $input.all()[0].json;

// Extract backlinks array from result
const backlinks = inputData.result.backlinks || [];

// Analyze and score each backlink opportunity
return backlinks.map(link => {
  // Calculate opportunity score based on multiple factors
  const domainAuth = parseInt(link.domain_authority) || 0;
  const hasRelevantAnchor = link.anchor_text && link.anchor_text.length > 0;
  const hasContext = link.context && link.context.length > 50;

  // Scoring algorithm
  let opportunityScore = 0;
  opportunityScore += domainAuth * 0.4; // 40% weight on domain authority
  opportunityScore += hasRelevantAnchor ? 20 : 0; // 20 points for anchor text
  opportunityScore += hasContext ? 15 : 0; // 15 points for context
  opportunityScore += Math.random() * 25; // 25 points random factor for content relevance

  return {
    json: {
      source_url: link.source_url,
      target_url: link.target_url,
      anchor_text: link.anchor_text,
      domain_authority: link.domain_authority,
      page_title: link.page_title,
      context: link.context,
      opportunity_score: Math.round(opportunityScore),
      priority: opportunityScore > 70 ? 'High' : opportunityScore > 40 ? 'Medium' : 'Low',
      analyzed_date: new Date().toISOString().split('T')[0]
    }
  };
});

// Merge backlink opportunity data with contact information
const backlinkData = $input.first().json;
const contactData = $input.last().json;

// Extract contact info from scraping result
const contacts = contactData.result || {};

// Create comprehensive outreach record
return [{
  json: {
    // Backlink opportunity data
    source_url: backlinkData.source_url,
    target_url: backlinkData.target_url,
    anchor_text: backlinkData.anchor_text,
    domain_authority: backlinkData.domain_authority,
    page_title: backlinkData.page_title,
    context: backlinkData.context,
    opportunity_score: backlinkData.opportunity_score,
    priority: backlinkData.priority,
    analyzed_date: backlinkData.analyzed_date,

    // Contact information
    emails: contacts.emails || [],
    contact_forms: contacts.contact_forms || [],
    social_media: contacts.social_media || {},
    team_members: contacts.team_members || [],

    // Outreach status
    outreach_status: 'pending',
    outreach_attempts: 0,
    last_contact_date: null,
    notes: '',

    // Generate personalized outreach message
    suggested_subject: `Partnership Opportunity - ${backlinkData.page_title}`,
    outreach_message: `Hi there,\n\nI came across your article "${backlinkData.page_title}" and found it really valuable. I noticed you mentioned ${backlinkData.anchor_text} - we have some related content that your readers might find helpful.\n\nWould you be interested in exploring a content partnership?\n\nBest regards`
  }
}];
