// Merge data from multiple competitor scrapers
const competitor1Data = $input.first().json;
const competitor2Data = $input.last().json;

// Extract articles from both competitors
const competitor1Articles = competitor1Data.result?.articles || [];
const competitor2Content = competitor2Data.result?.content_pieces || [];

// Normalize data structure for consistent processing
const normalizeContent = (content, source) => {
  return {
    title: content.title,
    url: content.url,
    publish_date: content.publish_date || content.publication_date,
    author: content.author || 'Unknown',
    word_count: parseInt(content.word_count || content.content_length) || 0,
    main_topic: content.main_topic || content.topic_cluster,
    keywords: content.keywords || content.target_keywords || [],
    content_type: content.content_type || content.content_format,
    categories: content.categories || [content.topic_cluster],
    meta_description: content.meta_description || '',
    headings: content.headings || [],
    social_shares: parseInt(content.social_shares) || 0,
    engagement_metrics: content.engagement_metrics || content.performance_indicators,
    competitor_source: source,
    analyzed_date: new Date().toISOString().split('T')[0]
  };
};

// Normalize all content
const allContent = [
  ...competitor1Articles.map(article => normalizeContent(article, 'competitor1')),
  ...competitor2Content.map(content => normalizeContent(content, 'competitor2'))
];

// Group content by main topics to identify clusters
const topicClusters = {};
allContent.forEach(content => {
  const topic = content.main_topic?.toLowerCase() || 'uncategorized';
  if (!topicClusters[topic]) {
    topicClusters[topic] = [];
  }
  topicClusters[topic].push(content);
});

// Analyze keyword frequency across all content
const keywordFrequency = {};
allContent.forEach(content => {
  content.keywords.forEach(keyword => {
    const normalizedKeyword = keyword?.toLowerCase();
    if (normalizedKeyword) {
      keywordFrequency[normalizedKeyword] = (keywordFrequency[normalizedKeyword] || 0) + 1;
    }
  });
});

// Return processed data for gap analysis
return [{
  json: {
    total_content_analyzed: allContent.length,
    competitor_sources: ['competitor1', 'competitor2'],
    topic_clusters: Object.keys(topicClusters).map(topic => ({
      topic_name: topic,
      content_count: topicClusters[topic].length,
      avg_word_count: Math.round(topicClusters[topic].reduce((sum, content) => sum + content.word_count, 0) / topicClusters[topic].length),
      top_keywords: topicClusters[topic].flatMap(c => c.keywords).slice(0, 10)
    })),
    trending_keywords: Object.entries(keywordFrequency)
      .sort(([,a], [,b]) => b - a)
      .slice(0, 20)
      .map(([keyword, frequency]) => ({ keyword, frequency })),
    content_formats: [...new Set(allContent.map(c => c.content_type))],
    all_content: allContent,
    analysis_summary: {
      most_covered_topics: Object.entries(topicClusters).sort(([,a], [,b]) => b.length - a.length).slice(0, 5).map(([topic, content]) => ({ topic, count: content.length })),
      average_content_length: Math.round(allContent.reduce((sum, content) => sum + content.word_count, 0) / allContent.length),
      content_types_distribution: allContent.reduce((acc, content) => {
        acc[content.content_type] = (acc[content.content_type] || 0) + 1;
        return acc;
      }, {})
    }
  }
}];

// Advanced Content Gap Analysis Algorithm
const competitorData = $input.first().json;
const ourContentData = $input.last().json;

// Extract our content data
const ourContent = ourContentData.result?.our_content || [];
const competitorContent = competitorData.all_content || [];

// Create comprehensive topic and keyword maps
const ourTopics = new Set(ourContent.map(c => c.main_topic?.toLowerCase()).filter(Boolean));
const ourKeywords = new Set(ourContent.flatMap(c => c.keywords || []).map(k => k?.toLowerCase()).filter(Boolean));

const competitorTopics = new Set(competitorContent.map(c => c.main_topic?.toLowerCase()).filter(Boolean));
const competitorKeywords = new Set(competitorContent.flatMap(c => c.keywords || []).map(k => k?.toLowerCase()).filter(Boolean));

// Identify content gaps
const topicGaps = [...competitorTopics].filter(topic => !ourTopics.has(topic));
const keywordGaps = [...competitorKeywords].filter(keyword => !ourKeywords.has(keyword));

// Analyze competitor content performance patterns
const highPerformingContent = competitorContent.filter(content => {
  const socialShares = parseInt(content.social_shares) || 0;
  const wordCount = parseInt(content.word_count) || 0;
  return socialShares > 100 || wordCount > 1500 || content.engagement_metrics === 'high';
});

// Identify content format gaps
const ourFormats = new Set(ourContent.map(c => c.content_type).filter(Boolean));
const competitorFormats = new Set(competitorContent.map(c => c.content_type).filter(Boolean));
const formatGaps = [...competitorFormats].filter(format => !ourFormats.has(format));

// Calculate content opportunity scores
const contentOpportunities = topicGaps.map(topic => {
  const relatedCompetitorContent = competitorContent.filter(c => c.main_topic?.toLowerCase() === topic);
  const avgWordCount = relatedCompetitorContent.reduce((sum, c) => sum + (parseInt(c.word_count) || 0), 0) / relatedCompetitorContent.length || 0;
  const totalSocialShares = relatedCompetitorContent.reduce((sum, c) => sum + (parseInt(c.social_shares) || 0), 0);
  const contentCount = relatedCompetitorContent.length;

  // Opportunity scoring algorithm
  let opportunityScore = 0;
  opportunityScore += Math.min(contentCount * 10, 50); // Content volume (max 50 points)
  opportunityScore += Math.min(totalSocialShares / 10, 30); // Social engagement (max 30 points)
  opportunityScore += avgWordCount > 1000 ? 20 : 10; // Content depth (10-20 points)

  return {
    topic,
    opportunity_score: Math.round(opportunityScore),
    competitor_content_count: contentCount,
    avg_word_count: Math.round(avgWordCount),
    total_social_engagement: totalSocialShares,
    priority: opportunityScore > 70 ? 'High' : opportunityScore > 40 ? 'Medium' : 'Low',
    suggested_content_types: [...new Set(relatedCompetitorContent.map(c => c.content_type))],
    top_keywords: relatedCompetitorContent.flatMap(c => c.keywords || []).slice(0, 5)
  };
}).sort((a, b) => b.opportunity_score - a.opportunity_score);

// Generate keyword-specific opportunities
const keywordOpportunities = keywordGaps.slice(0, 50).map(keyword => {
  const relatedContent = competitorContent.filter(c => 
    c.keywords?.some(k => k?.toLowerCase() === keyword)
  );

  return {
    keyword,
    frequency: relatedContent.length,
    avg_word_count: Math.round(relatedContent.reduce((sum, c) => sum + (parseInt(c.word_count) || 0), 0) / relatedContent.length) || 0,
    content_types: [...new Set(relatedContent.map(c => c.content_type))],
    related_topics: [...new Set(relatedContent.map(c => c.main_topic))]
  };
}).filter(opp => opp.frequency > 0).sort((a, b) => b.frequency - a.frequency);

return [{
  json: {
    gap_analysis_summary: {
      total_topic_gaps: topicGaps.length,
      total_keyword_gaps: keywordGaps.length,
      total_format_gaps: formatGaps.length,
      high_priority_opportunities: contentOpportunities.filter(o => o.priority === 'High').length,
      analysis_date: new Date().toISOString().split('T')[0]
    },
    content_opportunities: contentOpportunities.slice(0, 20), // Top 20 opportunities
    keyword_opportunities: keywordOpportunities.slice(0, 30), // Top 30 keyword gaps
    format_gaps: formatGaps,
    competitor_insights: {
      most_successful_topics: competitorData.analysis_summary?.most_covered_topics || [],
      trending_keywords: competitorData.trending_keywords?.slice(0, 15) || [],
      content_formats: competitorData.content_formats || []
    },
    our_content_analysis: {
      total_content_pieces: ourContent.length,
      our_topics: [...ourTopics],
      our_keywords_count: ourKeywords.size,
      our_formats: [...ourFormats]
    },
    recommendations: {
      immediate_actions: contentOpportunities.filter(o => o.priority === 'High').slice(0, 5),
      content_format_expansion: formatGaps.slice(0, 3),
      keyword_targeting: keywordOpportunities.slice(0, 10)
    }
  }
}];

// Keyword Mapping and SEO Opportunity Analysis
const gapData = $input.all()[0].json;

// Extract high-priority content opportunities
const contentOpportunities = gapData.content_opportunities || [];
const keywordOpportunities = gapData.keyword_opportunities || [];

// Create detailed keyword mapping for each content opportunity
const keywordMappedContent = contentOpportunities.map(opportunity => {
  // Find related keyword opportunities
  const relatedKeywords = keywordOpportunities.filter(kwOpp => 
    kwOpp.related_topics?.some(topic => topic?.toLowerCase().includes(opportunity.topic))
  );

  // Generate primary and secondary keyword recommendations
  const primaryKeywords = relatedKeywords.slice(0, 3).map(k => k.keyword);
  const secondaryKeywords = relatedKeywords.slice(3, 8).map(k => k.keyword);
  const longTailKeywords = relatedKeywords.slice(8, 15).map(k => k.keyword);

  // Calculate SEO difficulty and opportunity
  const avgCompetitorFrequency = relatedKeywords.reduce((sum, k) => sum + k.frequency, 0) / relatedKeywords.length || 0;
  const seoOpportunity = avgCompetitorFrequency < 5 ? 'High' : avgCompetitorFrequency < 10 ? 'Medium' : 'Low';

  return {
    topic: opportunity.topic,
    opportunity_score: opportunity.opportunity_score,
    priority: opportunity.priority,
    keyword_strategy: {
      primary_keywords: primaryKeywords,
      secondary_keywords: secondaryKeywords,
      long_tail_keywords: longTailKeywords,
      total_keyword_opportunities: relatedKeywords.length,
      seo_difficulty: seoOpportunity
    },
    content_specs: {
      recommended_word_count: opportunity.avg_word_count,
      suggested_formats: opportunity.suggested_content_types,
      competitor_benchmark: opportunity.competitor_content_count
    },
    search_intent_analysis: {
      informational_keywords: relatedKeywords.filter(k => 
        k.keyword.includes('how') || k.keyword.includes('what') || k.keyword.includes('why')
      ).map(k => k.keyword),
      commercial_keywords: relatedKeywords.filter(k => 
        k.keyword.includes('best') || k.keyword.includes('review') || k.keyword.includes('compare')
      ).map(k => k.keyword),
      transactional_keywords: relatedKeywords.filter(k => 
        k.keyword.includes('buy') || k.keyword.includes('price') || k.keyword.includes('discount')
      ).map(k => k.keyword)
    }
  };
});

// Generate keyword clusters for content planning
const keywordClusters = {};
keywordOpportunities.forEach(kwOpp => {
  kwOpp.related_topics?.forEach(topic => {
    if (!keywordClusters[topic]) {
      keywordClusters[topic] = [];
    }
    keywordClusters[topic].push({
      keyword: kwOpp.keyword,
      frequency: kwOpp.frequency,
      content_types: kwOpp.content_types
    });
  });
});

// SEO content recommendations
const seoRecommendations = {
  high_opportunity_keywords: keywordOpportunities.filter(k => k.frequency >= 3 && k.frequency <= 8).slice(0, 15),
  content_cluster_opportunities: Object.entries(keywordClusters)
    .filter(([topic, keywords]) => keywords.length >= 3)
    .map(([topic, keywords]) => ({
      cluster_topic: topic,
      pillar_content_opportunity: true,
      supporting_keywords: keywords.slice(0, 10),
      cluster_size: keywords.length
    })),
  competitive_gaps: keywordOpportunities.filter(k => k.frequency === 1).slice(0, 10) // Low competition keywords
};

return [{
  json: {
    keyword_mapped_opportunities: keywordMappedContent,
    keyword_clusters: Object.entries(keywordClusters).map(([topic, keywords]) => ({
      topic,
      keywords: keywords.slice(0, 15),
      cluster_strength: keywords.length
    })),
    seo_recommendations: seoRecommendations,
    content_strategy_insights: {
      pillar_page_opportunities: keywordMappedContent.filter(c => c.keyword_strategy.total_keyword_opportunities > 10).length,
      quick_wins: keywordMappedContent.filter(c => c.keyword_strategy.seo_difficulty === 'High').length,
      competitive_battles: keywordMappedContent.filter(c => c.keyword_strategy.seo_difficulty === 'Low').length
    },
    mapping_summary: {
      total_opportunities_mapped: keywordMappedContent.length,
      total_keywords_analyzed: keywordOpportunities.length,
      clusters_identified: Object.keys(keywordClusters).length,
      mapping_date: new Date().toISOString().split('T')[0]
    }
  }
}];

// Content Planning and Editorial Strategy Generator
const keywordData = $input.all()[0].json;

// Extract mapped opportunities
const keywordMappedOpportunities = keywordData.keyword_mapped_opportunities || [];
const keywordClusters = keywordData.keyword_clusters || [];
const seoRecommendations = keywordData.seo_recommendations || {};

// Generate detailed content plans for each opportunity
const contentPlans = keywordMappedOpportunities.map((opportunity, index) => {
  // Determine content format based on keywords and competition
  const suggestedFormat = opportunity.content_specs.suggested_formats?.[0] || 'blog post';

  // Calculate estimated effort and resources
  const wordCount = opportunity.content_specs.recommended_word_count || 1500;
  const estimatedHours = Math.ceil(wordCount / 250) + 2; // Writing + research + editing

  // Generate content outline based on keywords
  const outline = {
    introduction: `Introduction to ${opportunity.topic}`,
    main_sections: opportunity.keyword_strategy.primary_keywords.map(keyword => 
      `${keyword.charAt(0).toUpperCase() + keyword.slice(1)}: Comprehensive Guide`
    ),
    supporting_sections: opportunity.keyword_strategy.secondary_keywords.slice(0, 3).map(keyword => 
      `Understanding ${keyword}`
    ),
    conclusion: `Key Takeaways and Next Steps for ${opportunity.topic}`
  };

  // SEO optimization checklist
  const seoChecklist = {
    primary_keyword_density: '1-2%',
    meta_title: `${opportunity.keyword_strategy.primary_keywords[0]} - [Brand Name]`,
    meta_description: `Learn about ${opportunity.topic}. Comprehensive guide covering ${opportunity.keyword_strategy.primary_keywords.join(', ')}.`,
    header_structure: 'H1 (primary keyword) + H2s (secondary keywords) + H3s (long-tail)',
    internal_linking: 'Link to related existing content',
    external_linking: '2-3 authoritative sources',
    image_optimization: 'Alt text with target keywords'
  };

  return {
    content_id: `content-plan-${index + 1}`,
    title: `${opportunity.topic.charAt(0).toUpperCase() + opportunity.topic.slice(1)}: Complete Guide`,
    topic: opportunity.topic,
    priority: opportunity.priority,
    opportunity_score: opportunity.opportunity_score,
    content_format: suggestedFormat,
    target_keywords: {
      primary: opportunity.keyword_strategy.primary_keywords,
      secondary: opportunity.keyword_strategy.secondary_keywords,
      long_tail: opportunity.keyword_strategy.long_tail_keywords
    },
    content_specifications: {
      target_word_count: wordCount,
      estimated_hours: estimatedHours,
      difficulty_level: opportunity.keyword_strategy.seo_difficulty === 'High' ? 'Medium' : 'High',
      content_type: suggestedFormat,
      target_audience: 'Primary target demographic'
    },
    content_outline: outline,
    seo_optimization: seoChecklist,
    search_intent: {
      primary_intent: opportunity.search_intent_analysis.informational_keywords.length > 0 ? 'Informational' : 
                     opportunity.search_intent_analysis.commercial_keywords.length > 0 ? 'Commercial' : 'Transactional',
      secondary_intents: [
        ...(opportunity.search_intent_analysis.informational_keywords.length > 0 ? ['Informational'] : []),
        ...(opportunity.search_intent_analysis.commercial_keywords.length > 0 ? ['Commercial'] : []),
        ...(opportunity.search_intent_analysis.transactional_keywords.length > 0 ? ['Transactional'] : [])
      ]
    },
    competitive_analysis: {
      competitor_content_count: opportunity.content_specs.competitor_benchmark,
      differentiation_strategy: 'Create more comprehensive, actionable content',
      unique_angle: `Focus on practical implementation and real-world examples`
    },
    success_metrics: {
      primary_kpis: ['Organic traffic growth', 'Keyword ranking improvements', 'Social shares'],
      target_rankings: 'Top 10 for primary keywords within 6 months',
      engagement_goals: 'Above industry average time on page and bounce rate'
    }
  };
});

// Generate content calendar recommendations
const calendarRecommendations = {
  immediate_priority: contentPlans.filter(plan => plan.priority === 'High').slice(0, 5),
  medium_term: contentPlans.filter(plan => plan.priority === 'Medium').slice(0, 8),
  long_term: contentPlans.filter(plan => plan.priority === 'Low').slice(0, 10),
  pillar_content: contentPlans.filter(plan => plan.target_keywords.primary.length > 2).slice(0, 3),
  quick_wins: contentPlans.filter(plan => plan.content_specifications.difficulty_level === 'Medium').slice(0, 5)
};

// Resource planning
const resourcePlanning = {
  total_content_pieces: contentPlans.length,
  estimated_total_hours: contentPlans.reduce((sum, plan) => sum + plan.content_specifications.estimated_hours, 0),
  monthly_capacity: Math.ceil(contentPlans.length / 6), // Spread over 6 months
  content_formats_needed: [...new Set(contentPlans.map(plan => plan.content_format))],
  expertise_required: [...new Set(contentPlans.map(plan => plan.topic))]
};

return [{
  json: {
    content_plans: contentPlans,
    calendar_recommendations: calendarRecommendations,
    resource_planning: resourcePlanning,
    strategic_insights: {
      content_cluster_opportunities: keywordClusters.filter(cluster => cluster.cluster_strength > 5).length,
      seo_quick_wins: seoRecommendations.competitive_gaps?.length || 0,
      pillar_page_potential: keywordClusters.filter(cluster => cluster.cluster_strength > 10).length
    },
    execution_roadmap: {
      month_1: calendarRecommendations.immediate_priority.slice(0, 2),
      month_2: calendarRecommendations.immediate_priority.slice(2, 5),
      month_3: calendarRecommendations.medium_term.slice(0, 3),
      months_4_6: calendarRecommendations.medium_term.slice(3, 8)
    },
    planning_summary: {
      total_opportunities: contentPlans.length,
      high_priority_count: calendarRecommendations.immediate_priority.length,
      estimated_completion_timeline: '6 months',
      planning_date: new Date().toISOString().split('T')[0]
    }
  }
}];

// Editorial Calendar Generator with Timeline Planning
const contentData = $input.all()[0].json;

// Extract content plans and recommendations
const contentPlans = contentData.content_plans || [];
const executionRoadmap = contentData.execution_roadmap || {};
const resourcePlanning = contentData.resource_planning || {};

// Generate monthly editorial calendar
const generateEditorialCalendar = () => {
  const calendar = {};
  const currentDate = new Date();

  // Create 6-month calendar structure
  for (let i = 0; i < 6; i++) {
    const monthDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + i, 1);
    const monthKey = monthDate.toISOString().slice(0, 7); // YYYY-MM format
    const monthName = monthDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });

    calendar[monthKey] = {
      month_name: monthName,
      content_pieces: [],
      total_hours: 0,
      content_types: new Set(),
      priority_distribution: { High: 0, Medium: 0, Low: 0 }
    };
  }

  // Distribute content across months based on priority and roadmap
  const distributeContent = (content, monthOffset) => {
    const targetDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + monthOffset, 15);
    const monthKey = targetDate.toISOString().slice(0, 7);

    if (calendar[monthKey]) {
      // Calculate publication date (mid-month)
      const publicationDate = targetDate.toISOString().split('T')[0];

      // Create detailed calendar entry
      const calendarEntry = {
        content_id: content.content_id,
        title: content.title,
        topic: content.topic,
        priority: content.priority,
        content_format: content.content_format,
        estimated_hours: content.content_specifications.estimated_hours,
        target_word_count: content.content_specifications.target_word_count,
        primary_keywords: content.target_keywords.primary.join(', '),
        secondary_keywords: content.target_keywords.secondary.join(', '),
        publication_date: publicationDate,
        seo_difficulty: content.content_specifications.difficulty_level,
        opportunity_score: content.opportunity_score,

        // Production timeline
        research_start: new Date(targetDate.getTime() - 14 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        writing_start: new Date(targetDate.getTime() - 10 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
        review_date: new Date(targetDate.getTime() - 3 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],

        // Content specifications
        target_audience: content.search_intent.primary_intent,
        content_outline: Object.values(content.content_outline).join(' | '),
        success_metrics: content.success_metrics.primary_kpis.join(', '),

        // Status tracking
        status: 'Planned',
        assigned_writer: 'TBD',
        progress: 0,
        notes: `Gap analysis opportunity - compete with ${content.competitive_analysis.competitor_content_count} competitor pieces`
      };

      calendar[monthKey].content_pieces.push(calendarEntry);
      calendar[monthKey].total_hours += content.content_specifications.estimated_hours;
      calendar[monthKey].content_types.add(content.content_format);
      calendar[monthKey].priority_distribution[content.priority]++;
    }
  };

  // Distribute content according to roadmap
  (executionRoadmap.month_1 || []).forEach(content => distributeContent(content, 0));
  (executionRoadmap.month_2 || []).forEach(content => distributeContent(content, 1));
  (executionRoadmap.month_3 || []).forEach(content => distributeContent(content, 2));
  (executionRoadmap.months_4_6 || []).forEach((content, index) => {
    distributeContent(content, 3 + (index % 3));
  });

  // Convert Set to Array for content_types
  Object.keys(calendar).forEach(monthKey => {
    calendar[monthKey].content_types = [...calendar[monthKey].content_types];
  });

  return calendar;
};

const editorialCalendar = generateEditorialCalendar();

// Generate team workload analysis
const workloadAnalysis = Object.entries(editorialCalendar).map(([monthKey, monthData]) => ({
  month: monthData.month_name,
  total_content_pieces: monthData.content_pieces.length,
  total_hours: monthData.total_hours,
  avg_hours_per_piece: monthData.content_pieces.length > 0 ? Math.round(monthData.total_hours / monthData.content_pieces.length) : 0,
  content_formats: monthData.content_types,
  priority_breakdown: monthData.priority_distribution,
  capacity_status: monthData.total_hours > 160 ? 'Over Capacity' : monthData.total_hours > 120 ? 'High Load' : 'Manageable'
}));

// Create flat array for Google Sheets export
const calendarEntries = Object.values(editorialCalendar).flatMap(month => month.content_pieces);

// Generate editorial insights and recommendations
const editorialInsights = {
  total_content_planned: calendarEntries.length,
  total_estimated_hours: calendarEntries.reduce((sum, entry) => sum + entry.estimated_hours, 0),
  content_format_distribution: calendarEntries.reduce((acc, entry) => {
    acc[entry.content_format] = (acc[entry.content_format] || 0) + 1;
    return acc;
  }, {}),
  priority_distribution: calendarEntries.reduce((acc, entry) => {
    acc[entry.priority] = (acc[entry.priority] || 0) + 1;
    return acc;
  }, {}),
  avg_opportunity_score: Math.round(calendarEntries.reduce((sum, entry) => sum + entry.opportunity_score, 0) / calendarEntries.length),
  peak_production_months: workloadAnalysis.filter(month => month.capacity_status !== 'Manageable').map(month => month.month)
};

return calendarEntries.map(entry => ({ json: entry }));
