// SAFELY access config node data
const configNode = $('Config').first();
if (!configNode) {
  throw new Error("Missing 'Config' node or it's not accessible.");
}

const labelMap =  configNode.labelMap|| {};
const qaChecklist = configNode.qaChecklist || [];

console.log("Your message", labelMap);
console.log("Your message", qaChecklist);

// Access PR list from previous node
const prNode = $('Fetch All Merged PRs Since Last Tag').first(); // <-- Replace with your actual PR-fetch node name
if (!prNode || !prNode.json || !Array.isArray(prNode.json.items)) {
  throw new Error("Pull request data not found or improperly formatted.");
}

const prItems = prNode.json.items;

/** @type {Record<string, string[]>} */
const grouped = {};

// Loop through each PR
for (const pr of prItems) {
  const labels = pr.labels?.map(l => l.name) || [];
  const matchedKey = labels.find(l => labelMap[l]);
  const groupTitle = matchedKey ? labelMap[matchedKey] : "üìã Others";

  if (!grouped[groupTitle]) {
    grouped[groupTitle] = [];
  }

  grouped[groupTitle].push(`- ${pr.title} (#${pr.number})`);
}

// Build Markdown content
let markdown = `## üìù Release Notes\n`;

for (const group in grouped) {
  markdown += `\n### ${group}\n`;
  markdown += grouped[group].join("\n") + "\n";
}

markdown += `\n## ‚úÖ QA Checklist\n`;
markdown += qaChecklist.map(item => `[ ] ${item}`).join("\n");

return [
  {
    json: {
      release_notes: markdown,
    },
  },
];
