const input = $input.first().json.output

const cleanedInput = input.replace(/```json|```/g, '').trim();

// Parse the cleaned JSON
const parsedOutput = JSON.parse(cleanedInput);

// Extract the `post_content.text` and `image_prompt.description` details
const postContent = parsedOutput.post_content.text;
const imagePrompt = parsedOutput.image_prompt.description;

// Prepare the final output in the desired JSON structure
return {
  post_content: postContent,
  image_prompt: imagePrompt
};

const base64 = $('Text to image1').first().json.predictions[0].bytesBase64Encoded;
const uploadUrl = $input.first().json.value.uploadMechanism['com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest'].uploadUrl;
const asset = $input.first().json.value.asset;
const post_content = $('Normalizer').first().json.post_content || '';

// Validate input fields
if (!base64 || !uploadUrl || !asset) {
  throw new Error("Missing one of: decoded_image_buffer, uploadUrl, or asset.");
}

// Convert base64 string to binary buffer
const buffer = Buffer.from(base64, 'base64');

return [{
  binary: {
    data: {
      data: buffer,
      mimeType: 'image/png', // or 'image/jpeg' if needed
      fileName: 'image.png'
    }
  },
  json: {
    uploadUrl,
    asset,
    post_content
  }
}];

// n8n Code Node: Convert Markdown to LinkedIn Format
// This code processes markdown content from a specific field and converts it to LinkedIn-compatible format

// Get the markdown content from the specific field
let markdownContent = $('Convert to binary buffer').first().json.post_content || '';

  // Function to convert markdown to LinkedIn format
  function convertMarkdownToLinkedIn(content) {
    let linkedinContent = content;

    // 1. Handle bold text: **text** or __text__ -> text (LinkedIn doesn't support markdown bold)
    // But we can use Unicode bold characters or just keep it as regular text
    linkedinContent = linkedinContent.replace(/\*\*(.*?)\*\*/g, '$1');
    linkedinContent = linkedinContent.replace(/__(.*?)__/g, '$1');

    // 2. Handle italic text: *text* or _text_ -> text
    linkedinContent = linkedinContent.replace(/\*(.*?)\*/g, '$1');
    linkedinContent = linkedinContent.replace(/_(.*?)_/g, '$1');

    // 3. Handle numbered lists: convert 1ï¸âƒ£, 2ï¸âƒ£, 3ï¸âƒ£ format or regular numbered lists
    // Keep emoji numbers as they are, but handle regular numbered lists
    linkedinContent = linkedinContent.replace(/^\d+\.\s/gm, 'â€¢ ');

    // 4. Handle bullet points: convert - or * to â€¢
    linkedinContent = linkedinContent.replace(/^[\-\*]\s/gm, 'â€¢ ');

    // 5. Handle line breaks: convert \n to actual line breaks
    linkedinContent = linkedinContent.replace(/\\n/g, '\n');

    // 6. Handle headers: remove # symbols but keep the text prominent
    linkedinContent = linkedinContent.replace(/^#{1,6}\s/gm, '');

    // 7. Handle links: [text](url) -> text (url) or just text
    linkedinContent = linkedinContent.replace(/\[([^\]]+)\]\(([^\)]+)\)/g, '$1 ($2)');

    // 8. Handle code blocks: remove ``` or ` formatting
    linkedinContent = linkedinContent.replace(/```[\s\S]*?```/g, (match) => {
      return match.replace(/```/g, '');
    });
    linkedinContent = linkedinContent.replace(/`([^`]+)`/g, '$1');

    // 9. Clean up excessive line breaks (more than 2 consecutive)
    linkedinContent = linkedinContent.replace(/\n{3,}/g, '\n\n');

    // 10. Trim whitespace from start and end
    linkedinContent = linkedinContent.trim();

    // 11. Handle special LinkedIn formatting
    // LinkedIn supports emojis, so keep them as they are
    // LinkedIn supports line breaks, so maintain paragraph structure

    return linkedinContent;
  }

  // Function to add LinkedIn-specific enhancements
  function enhanceForLinkedIn(content) {
    let enhanced = content;

    // Add extra line break before emoji bullets for better readability
    enhanced = enhanced.replace(/([.!?])\s*([1-9]ï¸âƒ£)/g, '$1\n\n$2');

    // Ensure proper spacing around key sections
    enhanced = enhanced.replace(/(:)\n\n([1-9]ï¸âƒ£)/g, '$1\n\n$2');

    // Handle hashtag formatting - ensure they're on a new line
    const hashtagPattern = /#\w+/g;
    const hashtags = enhanced.match(hashtagPattern);

    if (hashtags) {
      // Remove hashtags from content
      enhanced = enhanced.replace(hashtagPattern, '').trim();

      // Add hashtags at the end with proper spacing
      enhanced += '\n\n' + hashtags.join(' ');
    }

    return enhanced;
  }

// Process the content
let processedContent = convertMarkdownToLinkedIn(markdownContent);
processedContent = enhanceForLinkedIn(processedContent);

// Return the single processed item
return {
  json: {
    original_content: markdownContent,
    linkedin_content: processedContent,
    processed_at: new Date().toISOString(),
    character_count: processedContent.length
  }
};

