/** 
* Loop for extracting the valid article URL from the   redirect URL
*/
for (const item of $input.all()) {
  /** Redirect URL */
  const rawLink = item.json.link;

  let extractedUrl = rawLink;

  /**
  * Actual URL is start from "&Url" to "&" 
  * It will match and extract the URL
  */
  const match = rawLink.match(/[?&]url=([^&]+)/);

  if (match && match[1]) {
    /** Decode the URL-encoded value */
    extractedUrl = decodeURIComponent(match[1]);
  }

  /** Replace the redirect URL with actual URL */
  item.json.link = extractedUrl;

}
return $input.all();

/**
 * This code will integrate the article link from Refactor article link node and output of get article page node
 */

/** Input for get articel page node */
const inputForBightData = $items("Refactor article link"); 

/** Output of get articel page node */
const outputOfBightData = $input.all(); // from the BrightData response


return outputOfBightData.map((item, index) => {
  const input = inputForBightData[index].json;
  const output = item.json;

  return {
    json: {
      ...output,
      link: input.link // Add the link from the original input
    }
  };
});

/** 
 * this code will generate the array of company details by using the row and unstructured data from previous node
 * It also remove the duplicate entry
*/

const results = [];
const seenCompanyNames = new Set();

function extractValidStartups(obj) {
  if (Array.isArray(obj)) {
    for (const item of obj) {
      extractValidStartups(item);
    }
  } else if (typeof obj === 'object' && obj !== null) {
    // Skip if it's an error object
    if (obj.error) return;

    // Check if it looks like a startup object
    if (obj.companyName) {
      const key = obj.companyName.trim().toLowerCase(); // normalize name
      if (!seenCompanyNames.has(key)) {
        seenCompanyNames.add(key);
        results.push({ json: obj });
      }
      return;
    }

    // Otherwise, recursively search its values
    for (const key in obj) {
      extractValidStartups(obj[key]);
    }
  }
}

for (const item of $input.all()) {
  const root = item.json[""];
  if (!Array.isArray(root)) continue;

  for (const entry of root) {
    extractValidStartups(entry);
  }
}

return results;
