    }
}];

// Assume the previous node output is available here
const inputSegments = $input.first().json.segments; // Accessing segments from the previous output

if (inputSegments.length > 0) {
    const lastSegment = inputSegments[inputSegments.length - 1].json;

    // Check if the last segment's duration is less than 2 seconds
    if (parseFloat(lastSegment.duration) < 2.0) {
        // Combine the last segment with the previous segment if it exists
        if (inputSegments.length > 1) {
            const secondLastSegment = inputSegments[inputSegments.length - 2].json;

            // Update the second last segment's words and duration
            secondLastSegment.words += ' ' + lastSegment.words;
            secondLastSegment.duration = (
                parseFloat(secondLastSegment.duration) +
                parseFloat(lastSegment.duration)
            ).toFixed(2); // Update duration

            // Remove the last segment as it has been merged
            inputSegments.pop();
        }
    }
}

// Return the updated segments
return [{ json: { segments: inputSegments } }];

// Set the segment length in seconds
const splitLength = 6;

// Extract the list of words from the input
const segmentWrapper = $input.first().json; // Get the JSON input
const wordList = segmentWrapper.Segments[0].data; // Access the words array

// Initialize variables
const segments = [];
let currentSegment = {
    id: 0,
    words: "",
    duration: 0
};
let currentStartTime = -1;
let totalDuration = 0;
let currentEndTime = 0;

// Define a small pause buffer for spacing between segments
const pauseBuffer = 0.1;

// Loop through each word and create segments
for (const wordObj of wordList) {
    const { word, start_time, end_time } = wordObj;

    // Set start time for the segment if not already set
    if (currentStartTime === -1) {
        currentStartTime = start_time;
    }

    // Add the current word to the segment
    currentSegment.words += (currentSegment.words ? ' ' : '') + word;

    // Update the segment's end time
    currentEndTime = end_time;

    // Calculate duration of the current segment
    currentSegment.duration = (currentEndTime - currentStartTime).toFixed(2);

    // If duration exceeds split length, finalize this segment
    if (currentSegment.duration >= splitLength) {
        currentSegment.duration = (parseFloat(currentSegment.duration) + pauseBuffer).toFixed(2);
        totalDuration += parseFloat(currentSegment.duration);
        segments.push(currentSegment);

        // Reset for the next segment
        currentSegment = {
            id: segments.length,
            words: "",
            duration: 0
        };
        currentStartTime = -1;
    }
}

// Handle the last segment if any words are left
if (currentSegment.words) {
    currentSegment.duration = (currentEndTime - currentStartTime).toFixed(2);
    currentSegment.duration = (parseFloat(currentSegment.duration) + pauseBuffer).toFixed(2);
    totalDuration += parseFloat(currentSegment.duration);
    segments.push(currentSegment);
}

// Calculate total duration in minutes and seconds
const roundedTotalDuration = Math.round(totalDuration);
const minutes = Math.floor(roundedTotalDuration / 60);
const seconds = roundedTotalDuration % 60;

// Use string concatenation instead of template literals (for n8n compatibility)
const totalRuntimeString = minutes + " minute" + (minutes !== 1 ? "s" : "") + ", " + seconds + " second" + (seconds !== 1 ? "s" : "");

// Return the output as an array of objects
return [
  {
    json: {
      segments: segments.map(segment => ({ json: segment })),
      totalDuration: roundedTotalDuration,
      TotalMinutes: totalRuntimeString
    }
  }
];

