function extractFileId(url) {
    const regex = /(?:drive|docs)\.google\.com\/.*\/d\/([a-zA-Z0-9_-]+)(?:[\/\?]|$)/;
    const match = url.match(regex);
    return match ? match[1] : null;
}

// Get the URLs from the input
const urls = $input.first().json['TTS Audio'];

// Check if the urls are in an array or string, and convert accordingly
let result = [];

if (Array.isArray(urls)) {
    // If 'urls' is an array, process each URL
    result = urls.map(url => {
        const fileId = extractFileId(url);
        return { fileId: fileId };
    });
} else if (typeof urls === 'string') {
    // If 'urls' is a string, process it as a single URL
    const fileId = extractFileId(urls);
    result = [{ fileId: fileId }];
} else {
    // Handle unexpected types (e.g., if it's neither an array nor a string)
    result = [{ error: 'Invalid input type for TTS Audio' }];
}

// Return the result as an array of objects
return result;
