return items.map(item => {
  let jsonStr = item.json.output;
  jsonStr = jsonStr.replace(/^```json\n?/, '').replace(/\n?```$/, '');

  try {
    const parsed = JSON.parse(jsonStr);
    return {
      json: {
        output: parsed
      }
    };
  } catch (error) {
    throw new Error('Invalid JSON string: ' + error.message);
  }
});
