// input: one item that contains the raw API JSON under $.json
// output: one item with assistant text at $.json.text
const run = items[0].json;          // ← what the HTTP Request / OpenAI node returned
let assistantText = null;

for (const chunk of run.output ?? []) {
  // assistants/Responses API format -------------------------------
  if (chunk.type === 'message') {
    const textPart = (chunk.message?.content || [])
      .find(p => p.type === 'text' || p.type === 'output_text');
    if (textPart?.text) { assistantText = textPart.text; break; }
  }
  // chat-completions fallback (older cheap models) -----------------
  if (Array.isArray(chunk.content)) {
    const textPart = chunk.content.find(p => p.text);
    if (textPart?.text) { assistantText = textPart.text; break; }
  }
}

return [{ json: { output: assistantText } }];

