parameters.jsCode:
const qdrantUrl = $('Set Node').first().json.qdrant_url;
const collectionName = $('Set Node').first().json.collection_name;

const results = [];

for (const item of items) {
  const url = item.json.searchResult.url;
  let isDuplicate = false;
  let duplicateType = null;
  let existingDocId = null;
  let skipProcessing = false;

  try {
    const response = await this.helpers.httpRequest({
      method: 'POST',
      url: `${qdrantUrl}/collections/${collectionName}/points/search`,
      body: {
        filter: {
          must: [
            {
              key: "metadata.doc_url",
              match: { value: url }
            }
          ]
        },
        limit: 1,
        with_payload: true
      },
      headers: { 'Content-Type': 'application/json' }
    });

    if (response.result && response.result.length > 0) {
      isDuplicate = true;
      duplicateType = 'url';
      existingDocId = response.result[0].payload.metadata.doc_id;
      skipProcessing = true;
    }
  } catch (error) {
    console.error('Error checking URL duplicates:', error);
    // If error, just treat as not a duplicate
  }

  results.push({
    json: {
      ...item.json,
      isDuplicate,
      duplicateType,
      existingDocId,
      skipProcessing
    }
  });
}

return results;

return items.map(item => {
  const rawText = item.json.markdown;

  // Clean up the text
  let cleanedText = rawText
    .replace(/!\[.*?\]\(.*?\)/g, '')      // Remove images
    .replace(/\[([^\]]+)\]\(([^)]+)\)/g, '$1 ($2)')  // [text](url) to text (url)
    .replace(/^#+\s?/gm, '')              // Remove headings
    .replace(/\*\*|__|\*|_/g, '')         // Remove bold/italic
    .replace(/<[^>]+>/g, '')              // Remove HTML tags
    .replace(/\s+/g, ' ')
    .trim();

  // Normalize
  let normalizedText = cleanedText
    .toLowerCase()
    .replace(/[^\w\s]/g, '')
    .replace(/\s+/g, ' ')
    .trim();

  // Copy everything but 'markdown'
  const { markdown, ...rest } = item.json;

  return {
    json: {
      ...rest,
      cleaned_text: cleanedText,
      normalized_text: normalizedText
    }
  };
});

