// Process all items in the input array, returning one output per input.
return items.map(item => {
  const md = item.json.markdown;

  let cleanedMarkdown = md;

  // If markdown is missing or not a string, return empty cleaned_markdown
  if (!cleanedMarkdown || typeof cleanedMarkdown !== "string") {
    return {
      json: {
        ...item.json,
        cleaned_markdown: ""
      }
    };
  }

  // Remove junk and navigation/footer content
  cleanedMarkdown = cleanedMarkdown
    .replace(/\[Skip to main content\]\([^)]+\)/gi, '')
    .replace(/Menu\s*\n+/gi, '')
    .replace(/An official website of the United States government[\s\S]+?Here‚Äôs how you know/gi, '')
    .replace(/Here‚Äôs how you know[\s\S]+?(?=\n#|$)/gi, '')
    .replace(/^\s*#+\s*About the author[\s\S]+$/gmi, '')
    .replace(/!\[.*?\]\(.*?\)/g, '')        // Remove images
    .replace(/^[-*_]{3,}\s*$/gm, '')        // Remove horizontal rules
    .replace(/^\s*[\r\n]/gm, '')            // Remove empty lines
    .replace(/\n{2,}/g, '\n')               // Remove repeated blank lines
    .trim();

  // Convert [text](url) links to "text (url)"
  cleanedMarkdown = cleanedMarkdown.replace(/\[([^\]]+?)\]\((https?:\/\/[^\)]+)\)/g, '$1 ($2)');

  // Remove reference-style links [1], [a], etc.
  cleanedMarkdown = cleanedMarkdown.replace(/\[[^\]]{1,3}\]/g, '');

  // Remove HTML tags
  cleanedMarkdown = cleanedMarkdown.replace(/<[^>]+>/g, '');

  // Normalize extra whitespace
  cleanedMarkdown = cleanedMarkdown.replace(/[ \t]+/g, ' ').replace(/\n{2,}/g, '\n').trim();

  return {
    json: {
      ...item.json,
      markdown: cleanedMarkdown
    }
  };
});

// Aggregate all input items under "summaries"
return [
  {
    json: {
      summaries: items.map(i => i.json.output)
    }
  }
];

// Aggregate all input items under "summaries"
