// Get the consistent context from the start of the workflow.
const context = $('Set: Context for Response').item.json;

// Get the result data from the node that just ran (e.g., Set CHECK Output, Set ADD Output, Set UPDATE Output, Set DELETE Output)
const resultData = $input.item.json;

// Build the base log payload
const logData = {
  timestamp: new Date().toISOString(),
  utilityName: 'ItemTracker',
  action: context.action,
  keyPrefix: context.keyPrefix,
  itemId: context.itemId,
  fullKey: context.fullKey,
  ttl_seconds: context.TTL || null,
  status: 'Success',
  parentExecution: context.parentExecution || null // Keep the full parentExecution object as is for comprehensive logging
};

// Add the specific result of the 'check' action to the log
if (logData.action === 'check') {
  logData.result = {
    isMember: resultData.isMember
  };
}

// Determine status and add action-specific results for logging
let status = 'Success'; // Default status for logging

if (context.action === 'check') {
  status = resultData.isMember ? 'Item_Found' : 'Item_Not_Found';
  // Result for 'check' is already set above
} else if (context.action === 'add') {
  status = 'In_Progress_Marked';
} else if (context.action === 'update') {
  status = resultData.status; // Get status from the updated value (e.g., 'completed_success', 'failed')
  if (resultData.errorMessage) {
    logData.error = { message: resultData.errorMessage, details: resultData.errorDetails };
  }
} else if (context.action === 'delete') {
  status = 'Tracking_Removed';
}

logData.status = status; // Set the determined status for the log

// --- START: FINAL Parent Workflow Execution URL Construction (Troubleshoot Link) ---
const parentExecContext = context.parentExecution || {};
let troubleshootExecutionUrl = null;

// The most direct link to the execution details page
if (parentExecContext.workflowId && parentExecContext.id) {
    troubleshootExecutionUrl = `https://centralise.app.n8n.cloud/workflow/${parentExecContext.workflowId}/executions/${parentExecContext.id}`;
} else if (parentExecContext.url && parentExecContext.id) {
    // Fallback if workflowId isn't separated, try to construct from base URL and ID
    const baseUrl = parentExecContext.url.split('/workflow/')[0]; // Extract base URL if 'url' contains workflow path
    troubleshootExecutionUrl = `${baseUrl}/workflow/${parentExecContext.workflowId || 'UNKNOWN_WF_ID'}/executions/${parentExecContext.id}`;
}
// Note: parentExecContext.resumeUrl is explicitly NOT used here as the primary "troubleshoot" link
// as it's for resuming, not for viewing execution details.

if (troubleshootExecutionUrl) {
    logData.troubleshootExecutionUrl = troubleshootExecutionUrl; // Add to the log message
}
// --- END: FINAL Parent Workflow Execution URL Construction ---


// Explicitly construct the final output object to ensure correctness.
const finalOutput = {
  success: true, // Always true if the utility workflow completes without crashing
  actionPerformed: context.action,
  itemId: context.itemId,
  isMember: resultData.isMember, // Only relevant for 'check' action

  // For 'update' action, pass relevant status/error back:
  status: context.action === 'update' ? resultData.status : undefined,
  errorMessage: context.action === 'update' ? resultData.errorMessage : undefined,
  errorDetails: context.action === 'update' ? resultData.errorDetails : undefined,

  // Add the direct troubleshoot URL to the final output
  troubleshootExecutionUrl: troubleshootExecutionUrl // <-- FINAL, CORRECT FIELD FOR OUTPUT
};

// Add the log message to the final output object
finalOutput.logMessage = JSON.stringify(logData);

// Return the final, complete object
return [{
  json: finalOutput
}];

