return items.map(item => {
  const fullUrl = item.json.channel_id; // contoh: "https://www.youtube.com/channel/UCxxxxxxx"
  const parts = fullUrl.split('/');
  const channelId = parts[parts.length - 1];

  return {
    json: {
      channel_id: channelId,
      rss_url: `https://www.youtube.com/feeds/videos.xml?channel_id=${channelId}`,
    }
  };
});

const transcriptParts = items[0].json.content; // Ambil hanya dari 1 item (karena semua content ada di dalam satu objek)

const transcript = transcriptParts
  .map(part => part.text)
  .filter(Boolean) // Buang null atau undefined
  .join(' ');

return [
  {
    json: {
      full_transcript: transcript
    }
  }
];

