// Static or dynamic employee information
const profile = $('On form submission').first().json;
const employee = {
  name: profile["Employee Name"],
  department: profile["Department"],
  tripPurpose: profile["Trip Purpose"],
  fromDate: profile["From Date"],
  toDate: profile["To Date"],
};

// Collect parsed expense outputs
const items = $input.all();
const expenses = items.map((item, index) => {
  const data = item.json.output;

  return {
    index: index + 1,
    expenseType: data.expense_type || "",
    vendor: data.vendor_name || "",
    invoiceNumber: data.invoice_number || "",
    receiptNumber: data.receipt_number || "",
    issueDate: data.issue_date || "",
    totalAmount: data.total_amount || 0,
    currency: data.currency || "",
    taxAmount: data.tax_amount || 0,
    paymentMethod: data.payment_method || "",
    location: data.location || "",
    notes: data.notes || "",
    items: data.itemized_descriptions || []
  };
});

return [
  {
    json: {
      employee,
      expenses
    }
  }
];

const data = $input.item.json;
const binaryData = $input.item.binary;

let output = [];

Object.keys(binaryData)
  .filter(label => label.startsWith("Receipts___Invoices_"))
  .forEach(label => {
    output.push({
      json: data,
      binary: { data: binaryData[label] }
    });
  });

return output;
