// Get all URLs and tutorials
const urlsAndTitles = $("Extract Tanscript+url+output+title").all().map(item => item.json)


// Safely get items from a node, handling cases where the node didn't execute
function safeGetAll(nodeName) {
  try {
    return $(nodeName).all() || [];
  } catch (error) {
    console.log(`Node ${nodeName} not executed or error: ${error.message}`);
    return [];
  }
}

// Create an array of matched pairs
const matchedItems = urlsAndTitles.map((item, index) => {
  return {
    url: item.url,
    title:item.title,
    tutorial: safeGetAll("tutorial").map(item => item.json.text)[index] || "",
    youtubeItem: safeGetAll("youtube script").map(item => item.json)[index] || "",
    summary: safeGetAll("Summary").map(item => item.json)[index] || "",
    linkedinPost: safeGetAll("linkedin").map(item => item.json.text)[index] || "",
    tweet: safeGetAll("tweet").map(item => item.json.text)[index] || "",
    newsletter: safeGetAll("newsletter").map(item => item.json.text)[index] || "",
    blogPost: safeGetAll("blogpost").map(item => item.json.text)[index] || "",
  }
});

return matchedItems;
