const places = localResults.map(place => {
  // Extract additional data that might be useful for analysis
  const placeData = {
    place_name: place.title || 'Unknown',
    address: place.address || 'No address',
    rating: place.rating || 'No rating',
    reviews_count: place.reviews || 0,
    reviews_link: place.reviews_link || null,
    data_id: place.data_id || null,
    type: place.type || 'Unknown',
    place_id: place.place_id || null,
    lsig: place.lsig || null,
    phone: place.phone || null,
    website: place.website || null,
    thumbnail: place.thumbnail || null,
    operating_hours: place.operating_hours || null,
