// Function to calculate how complete the place data is
function calculateCompletenessScore(placeData) {
  const fields = ['place_name', 'address', 'rating', 'phone', 'website', 'operating_hours'];
  const filledFields = fields.filter(field => 
    placeData[field] && placeData[field] !== 'Unknown' && placeData[field] !== 'No address'
  );
  return Math.round((filledFields.length / fields.length) * 100);
}

console.log(`Processing ${combinedData.analysis_metadata.analysis_focus}: ${combinedData.place_name}`);
console.log(`Data completeness: ${combinedData.analysis_metadata.place_completeness_score}%`);

return { json: combinedData };

// Enhanced data preparation with flexible analysis structure
const allPlaces = $input.all();
const userConfig = $('User Input Configuration').item.json;

console.log(`Preparing analysis for ${allPlaces.length} ${userConfig.analysis_focus} locations in ${userConfig.city_name}`);

// Create comprehensive analysis dataset
let analysisPrompt = `MARKET RESEARCH DATA ANALYSIS\n`;
analysisPrompt += `Research Focus: ${userConfig.analysis_focus}\n`;
analysisPrompt += `Location: ${userConfig.city_name}\n`;
analysisPrompt += `Search Query: "${userConfig.search_query}"\n`;
analysisPrompt += `Total Locations Found: ${allPlaces.length}\n`;
analysisPrompt += `Analysis Date: ${new Date().toLocaleDateString('vi-VN')}\n\n`;

// Enhanced data summary
analysisPrompt += `=== DATASET OVERVIEW ===\n`;
let totalReviews = 0;
let avgRating = 0;
let placesWithReviews = 0;
let completenessScores = [];

allPlaces.forEach((place, index) => {
  const data = place.json;

  // Calculate statistics
  if (data.reviews_count && typeof data.reviews_count === 'number') {
    totalReviews += data.reviews_count;
  }

  if (data.rating && data.rating !== 'No rating') {
    avgRating += parseFloat(data.rating) || 0;
  }

  if (data.reviews_content && data.reviews_content.reviews) {
    placesWithReviews++;
  }

  if (data.analysis_metadata && data.analysis_metadata.place_completeness_score) {
    completenessScores.push(data.analysis_metadata.place_completeness_score);
  }

  // Add detailed place information
  analysisPrompt += `\n${index + 1}. ${data.place_name}\n`;
  analysisPrompt += `   Address: ${data.address}\n`;
  analysisPrompt += `   Rating: ${data.rating}/5 (${data.reviews_count || 0} reviews)\n`;

  // Add contact and operational info if available
  if (data.phone) analysisPrompt += `   Phone: ${data.phone}\n`;
  if (data.website) analysisPrompt += `   Website: ${data.website}\n`;
  if (data.operating_hours) analysisPrompt += `   Hours: ${JSON.stringify(data.operating_hours)}\n`;
  if (data.price_level) analysisPrompt += `   Price Level: ${data.price_level}\n`;

  // Add review samples if available
  if (data.reviews_content && data.reviews_content.reviews) {
    analysisPrompt += `   Recent Reviews:\n`;
    data.reviews_content.reviews.slice(0, 5).forEach(review => {
      const reviewText = review.snippet || review.text || review.comment || 'No text available';
      const reviewRating = review.rating || 'No rating';
      analysisPrompt += `   - "${reviewText.substring(0, 200)}..." (${reviewRating}/5)\n`;
    });
  }

  analysisPrompt += `   Data Completeness: ${data.analysis_metadata?.place_completeness_score || 'N/A'}%\n`;
  analysisPrompt += `\n${'-'.repeat(60)}\n`;
});

// Add summary statistics
const avgRatingCalculated = allPlaces.length > 0 ? (avgRating / allPlaces.length).toFixed(2) : 0;
const avgCompleteness = completenessScores.length > 0 ? 
  (completenessScores.reduce((a, b) => a + b, 0) / completenessScores.length).toFixed(1) : 'N/A';

analysisPrompt += `\n=== SUMMARY STATISTICS ===\n`;
analysisPrompt += `Total Places Analyzed: ${allPlaces.length}\n`;
analysisPrompt += `Places with Review Data: ${placesWithReviews}\n`;
analysisPrompt += `Total Reviews Collected: ${totalReviews}\n`;
analysisPrompt += `Average Rating: ${avgRatingCalculated}/5\n`;
analysisPrompt += `Average Data Completeness: ${avgCompleteness}%\n`;

return {
  json: {
    analysis_prompt: analysisPrompt,
    raw_data: allPlaces.map(place => place.json),
    summary_stats: {
      total_places: allPlaces.length,
      places_with_reviews: placesWithReviews,
      total_reviews: totalReviews,
      average_rating: avgRatingCalculated,
      average_completeness: avgCompleteness,
      search_context: userConfig
    }
  }
};

