function formatToHTML(content) {
  let html = content
    // Convert markdown-style formatting
    .replace(/\*\*([^*]+)\*\*/g, '<strong>$1</strong>')
    .replace(/\*([^*]+)\*/g, '<em>$1</em>')

    // Convert main sections (I., II., III., etc.)
    .replace(/\*\*([IVX]+\. [^*]+)\*\*/g, '<h2 style="color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 8px; margin-top: 30px;">$1</h2>')

    // Convert subsections
    .replace(/\*\*([^*]+:)\*\*/g, '<h3 style="color: #34495e; margin-top: 20px; margin-bottom: 10px;">$1</h3>')

    // Convert numbered lists with enhanced styling
    .replace(/^(\d+\. )/gm, '<div style="margin: 8px 0;"><strong style="color: #3498db;">$1</strong>')

    // Convert bullet points with different levels
    .replace(/^- /gm, '<div style="margin-left: 20px; margin: 5px 0;">‚Ä¢ ')
    .replace(/^  - /gm, '<div style="margin-left: 40px; margin: 5px 0;">‚ó¶ ')
    .replace(/^    - /gm, '<div style="margin-left: 60px; margin: 5px 0;">‚ñ™ ')

    // Close div tags and handle line breaks
    .replace(/(‚Ä¢ [^<\n]+)(?=\n|$)/g, '$1</div>')
    .replace(/(‚ó¶ [^<\n]+)(?=\n|$)/g, '$1</div>')
    .replace(/(‚ñ™ [^<\n]+)(?=\n|$)/g, '$1</div>')
    .replace(/\n\n/g, '</p><p>')
    .replace(/\n/g, '<br>');

  return '<p>' + html + '</p>'.replace(/<p><\/p>/g, '');
