// Simple blockchain data preparation
const input = $input.all()[0].json;

// Basic normalization
const blockData = {
  block_number: input.blockNumber || input.block_number || input.height,
  blockchain: (input.blockchain || input.chain || 'ethereum').toLowerCase(),
  timestamp: input.timestamp || Date.now(),
  session_id: `block_${Date.now()}`
};

// Set explorer URL
const explorers = {
  ethereum: `https://etherscan.io/block/${blockData.block_number}`,
  bitcoin: `https://blockchair.com/bitcoin/block/${blockData.block_number}`,
  bsc: `https://bscscan.com/block/${blockData.block_number}`,
  polygon: `https://polygonscan.com/block/${blockData.block_number}`
};

blockData.explorer_url = explorers[blockData.blockchain] || explorers.ethereum;

console.log(`Processing block ${blockData.block_number} on ${blockData.blockchain}`);

return [{ json: blockData }];

// Simple risk analysis
const blockData = $('Normalize Data').first().json;
const scrapedData = $input.all()[0].json;

// Initialize analysis
let analysis = {
  block_number: blockData.block_number,
  blockchain: blockData.blockchain,
  timestamp: new Date().toISOString(),

  // Basic stats
  total_transactions: 0,
  total_volume_usd: 0,
  high_value_transactions: [],
  failed_transactions: 0,

  // Risk assessment
  risk_score: 0,
  risk_level: 'low',
  alerts: []
};

// Process scraped data
if (scrapedData.transactions && Array.isArray(scrapedData.transactions)) {
  analysis.total_transactions = scrapedData.transactions.length;

  scrapedData.transactions.forEach(tx => {
    const value = parseFloat(tx.value_usd || 0);
    analysis.total_volume_usd += value;

    // High-value detection
    if (value > 10000) {
      analysis.high_value_transactions.push({
        hash: tx.hash,
        value_usd: value,
        from: tx.from,
        to: tx.to
      });
    }

    // Failed transaction count
    if (tx.status === 'failed') {
      analysis.failed_transactions++;
    }
  });
}

// Use summary data if available
if (scrapedData.summary) {
  analysis.total_transactions = scrapedData.summary.total_transactions || analysis.total_transactions;
  analysis.total_volume_usd = scrapedData.summary.total_volume_usd || analysis.total_volume_usd;
  analysis.failed_transactions = scrapedData.summary.failed_count || analysis.failed_transactions;
}

// Simple risk scoring
let riskScore = 0;

// High value risk
riskScore += analysis.high_value_transactions.length * 15;

// Volume risk
if (analysis.total_volume_usd > 1000000) riskScore += 20;
else if (analysis.total_volume_usd > 100000) riskScore += 10;

// Failure rate risk
const failureRate = analysis.total_transactions > 0 ? 
  analysis.failed_transactions / analysis.total_transactions : 0;
if (failureRate > 0.1) riskScore += 15;

analysis.risk_score = Math.min(riskScore, 100);

// Risk level determination
if (analysis.risk_score >= 50) {
  analysis.risk_level = 'high';
} else if (analysis.risk_score >= 25) {
  analysis.risk_level = 'medium';
} else {
  analysis.risk_level = 'low';
}

// Generate alerts
if (analysis.risk_level === 'high') {
  analysis.alerts.push(`High risk block: ${analysis.high_value_transactions.length} high-value transactions`);
}

if (analysis.high_value_transactions.length > 3) {
  analysis.alerts.push(`Multiple high-value transactions detected`);
}

if (failureRate > 0.2) {
  analysis.alerts.push(`High failure rate: ${Math.round(failureRate * 100)}%`);
}

console.log(`Analysis: ${analysis.total_transactions} txs, $${analysis.total_volume_usd.toLocaleString()}, Risk: ${analysis.risk_level}`);

return [{ json: analysis }];
