const results = [];

for (const item of items) {
  const data = item.json;

  const endDate = new Date(data["Contract End Date"]);
  const noticePeriod = parseInt(data["Notice Period (days)"], 10);

  // Calculate the date when the reminder should be sent
  const reminderDate = new Date(endDate);
  reminderDate.setDate(reminderDate.getDate() - noticePeriod);

  // If today's date >= reminder date, it's time to remind
  if ($today >= reminderDate && $today <= endDate) {
    results.push({
      json: {
        ...data,
        remind: true,
        reminderDate: reminderDate.toISOString().split("T")[0],
        daysUntilExpiry: Math.floor((endDate - $today) / (1000 * 60 * 60 * 24))
      }
    });
  }
}

return results;

