// This code gets all the news items that passed the filter.
const allItems = $input.all();

// We will format each news item with its title, content, and a link.
const formattedNews = allItems.map(item => {
  // Get the title, content, and link from the JSON data of each item.
  const title = item.json.title;
  const content = item.json.content;
  const link = item.json.link;

  // Return a clean, formatted string for each article.
  return `
---
Title: ${title}
Content: ${content}
Link: ${link}
---
  `;
});

// --- NEW CODE ADDED BELOW ---
// Get today's date and format it beautifully.
// It will look like: "July 31, 2025"
const digestDate = $now.setZone('America/Sao_Paulo').toFormat('MMMM d, yyyy');

// Join all the formatted news items into a single block of text
// and return it as an object along with our new date string.
return {
  allNews: formattedNews.join('\n'),
  digestDate: digestDate
};

/**
 * This Function node takes the script from the previous node
 * and splits it using "voice1:" and "voice2:" as delimiters.
 * Each resulting segment retains the respective identifier.
 */

const script = $input.first().json.output|| "";

// Ensure consistent line breaks
const normalizedScript = script.replace(/\r\n/g, "\n");

// Split the script while keeping "voice1:" and "voice2:" in the result
const segments = normalizedScript.split(/(?=(?:voice1:|voice2:))/g).map(s => s.trim()).filter(Boolean);

// Return one item per segment
return segments.map(segment => {
  return {
    json: {
      segment
    }
  };
});

