// This code runs for each item passed to the node.
// We assume the input is the single item containing the customer and their invoices.
const item = $items("Get Customer Wise Invoice list")[0];

// Extract the customer name and the array of invoices
const customerName = item.json.customer;
const invoices = item.json.invoices;

// --- 1. Process the Invoices ---

let totalAmountDue = 0;
let invoiceRows = ''; // This will hold the HTML for the table rows

// Loop through each invoice to build the table rows and calculate the total amount
for (const invoice of invoices) {
  totalAmountDue += invoice.json.TotalAmt; // Add the invoice amount to the total

  // Create an HTML table row for the current invoice
  invoiceRows += `
    <tr style="border-bottom: 1px solid #ddd;">
      <td style="padding: 12px; text-align: left;">${invoice.json.DocNumber}</td>
      <td style="padding: 12px; text-align: left;">${invoice.json.TxnDate}</td>
      <td style="padding: 12px; text-align: left;">${invoice.json.DueDate}</td>
      <td style="padding: 12px; text-align: right; font-weight: bold;">$${invoice.json.TotalAmt.toFixed(2)}</td>
    </tr>
  `;
}

// Format the final total to 2 decimal places
const formattedTotal = totalAmountDue.toFixed(2);

// --- 2. Build the HTML Email Template ---

const emailHtml = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body {
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif;
      margin: 0;
      padding: 0;
      background-color: #f4f4f4;
    }
    .email-container {
      max-width: 600px;
      margin: 20px auto;
      background-color: #ffffff;
      border: 1px solid #e0e0e0;
      border-radius: 8px;
      overflow: hidden;
    }
    .email-header {
      background-color: #007bff;
      color: #ffffff;
      padding: 20px;
      text-align: center;
    }
    .email-header h1 {
      margin: 0;
      font-size: 24px;
    }
    .email-body {
      padding: 30px;
      line-height: 1.6;
      color: #333333;
    }
    .email-body p {
      margin: 0 0 15px 0;
    }
    .invoice-table {
      width: 100%;
      border-collapse: collapse;
      margin: 20px 0;
    }
    .invoice-table th {
      background-color: #f2f2f2;
      padding: 12px;
      text-align: left;
      border-bottom: 2px solid #ddd;
      color: #555;
    }
    .total-row td {
      padding: 15px 12px;
      font-size: 18px;
      font-weight: bold;
      border-top: 2px solid #007bff;
    }
    .email-footer {
      text-align: center;
      padding: 20px;
      font-size: 12px;
      color: #888888;
      background-color: #f4f4f4;
    }
    .cta-button {
      display: inline-block;
      background-color: #28a745;
      color: #ffffff;
      padding: 12px 25px;
      margin-top: 20px;
      border-radius: 5px;
      text-decoration: none;
      font-weight: bold;
    }
  </style>
</head>
<body>
  <div class="email-container">
    <div class="email-header">
      <h1>Payment Reminder</h1>
    </div>
    <div class="email-body">
      <p>Dear ${customerName},</p>
      <p>We're writing to kindly remind you about the outstanding balance on your account. We have listed the unpaid invoices below for your convenience.</p>

      <table class="invoice-table">
        <thead>
          <tr>
            <th style="width: 25%;">Invoice #</th>
            <th style="width: 25%;">Date</th>
            <th style="width: 25%;">Due Date</th>
            <th style="width: 25%; text-align: right;">Amount</th>
          </tr>
        </thead>
        <tbody>
          ${invoiceRows}
        </tbody>
      </table>

      <table style="width: 100%; text-align: right;">
        <tbody>
          <tr class="total-row">
            <td>Total Amount Due: $${formattedTotal}</td>
          </tr>
        </tbody>
      </table>

      <p>If you have already made the payment, please disregard this email. If you have any questions about your invoices, feel free to contact us at any time.</p>
      <p>Thank you for your business!</p>
      <a href="https://your-payment-portal-link.com" class="cta-button">Pay Now</a>
    </div>
    <div class="email-footer">
      <p>Your Company Name | 123 Business Rd, Business City, 12345</p>
    </div>
  </div>
</body>
</html>
`;

// --- 3. Return the HTML for the next node ---
// The generated HTML is placed in the 'emailBody' property
// This can be easily used in the 'HTML' field of a 'Send Email' node with an expression like: {{$json.emailBody}}
item.json.emailBody = emailHtml;

return item;

const getInvoices = $items("Get Unpaid Invoices");

const invoices = []
const customerId = getInvoices[0].json.CustomerRef.value;
const customerName = getInvoices[0].json.CustomerRef.name;

for (const item of getInvoices) {
  // Access the invoices array from the JSON property of the current item

    if(customerId == item.json.CustomerRef.value && item.json.Balance > 0) {
      invoices.push(item)
    }

}


return {
      "customer": customerName,
      "customerId": customerId,
      "invoices": invoices
    };
