function createDynamicConnections(consolidatedWorkflows) {
    try {
        // ═══════════════════════════════════════════════════════════════
        // SECTION 1: EXTRACTION ET CORRECTION DES IDS DUPLIQUÉS
        // ═══════════════════════════════════════════════════════════════
        const fragments = consolidatedWorkflows.split('--- FRAGMENT SEPARATOR ---');
        const allNodes = [];
        const nodesBySignature = new Map();
        const usedIds = new Set();
        let idCounter = 1;

        fragments.forEach(fragment => {
            try {
                const parsed = JSON.parse(fragment.trim());
                if (parsed.nodes) {
                    parsed.nodes.forEach(node => {
                        // CORRIGER LES IDS DUPLIQUÉS
                        let uniqueId = node.id;
                        if (usedIds.has(uniqueId)) {
                            uniqueId = `node-${idCounter}`;
                            while (usedIds.has(uniqueId)) {
                                idCounter++;
                                uniqueId = `node-${idCounter}`;
                            }
                        }
                        usedIds.add(uniqueId);
                        idCounter++;

                        const nodeInfo = {
                            nodeId: uniqueId,
                            originalId: node.id,
                            nodeType: node.type,
                            signature: parsed.golden_dev_signature || 'unknown',
                            nodeName: node.name || 'Unknown',
                            parameters: node.parameters || {}
                        };

                        allNodes.push(nodeInfo);

                        // Mapper par signature pour référence rapide
                        if (parsed.golden_dev_signature) {
                            nodesBySignature.set(parsed.golden_dev_signature, nodeInfo);
                        }
                    });
                }
            } catch (e) {
                // Ignorer fragments invalides
                console.log('Fragment ignoré:', e.message);
            }
        });

        // ═══════════════════════════════════════════════════════════════
        // SECTION 2: CLASSIFICATION INTELLIGENTE DES NŒUDS
        // ═══════════════════════════════════════════════════════════════
        const nodeCategories = {
            triggers: [],
            processors: [],
            routers: [],
            actions: [],
            outputs: []
        };

        allNodes.forEach(node => {
            const type = node.nodeType.toLowerCase();
            const name = node.nodeName.toLowerCase();

            // CLASSIFICATION PAR PRIORITÉ (éviter les doublons)
            if (type.includes('respond') || name.includes('respond')) {
                nodeCategories.outputs.push(node);
            } else if (type.includes('webhook') || type.includes('trigger') || type.includes('manual')) {
                nodeCategories.triggers.push(node);
            } else if (type.includes('switch') || name.includes('switch') || name.includes('router')) {
                nodeCategories.routers.push(node);
            } else if (type.includes('function') || type.includes('http') || type.includes('code') || type.includes('mcp')) {
                nodeCategories.processors.push(node);
            } else {
                // Tout le reste = actions (email, salesforce, slack, set, gmail, etc.)
                nodeCategories.actions.push(node);
            }
        });

        // ═══════════════════════════════════════════════════════════════
        // SECTION 3: CRÉATION DES CONNEXIONS LOGIQUES SANS BOUCLES
        // ═══════════════════════════════════════════════════════════════
        const connections = {};

        // 3.1 TRIGGERS → PROCESSORS (point d'entrée)
        if (nodeCategories.triggers.length > 0 && nodeCategories.processors.length > 0) {
            const mainTrigger = nodeCategories.triggers.find(t => 
                t.nodeType.includes('webhook') || t.signature.includes('webhook')
            ) || nodeCategories.triggers[0];

            const firstProcessor = nodeCategories.processors[0];

            connections[mainTrigger.nodeId] = {
                main: [[{ node: firstProcessor.nodeId, type: "main", index: 0 }]]
            };
        }

        // 3.2 PROCESSORS → PROCESSORS → ROUTERS (chaînage séquentiel)
        nodeCategories.processors.forEach((processor, index) => {
            const nextProcessor = nodeCategories.processors[index + 1];
            const router = nodeCategories.routers[0];

            if (nextProcessor) {
                connections[processor.nodeId] = {
                    main: [[{ node: nextProcessor.nodeId, type: "main", index: 0 }]]
                };
            } else if (router) {
                // Dernier processor vers router
                connections[processor.nodeId] = {
                    main: [[{ node: router.nodeId, type: "main", index: 0 }]]
                };
            }
        });

        // 3.3 ROUTERS → ACTIONS (branches multiples du switch)
        nodeCategories.routers.forEach(router => {
            const routerConnections = [];

            // Créer une branche pour chaque action
            nodeCategories.actions.forEach(action => {
                routerConnections.push([{ node: action.nodeId, type: "main", index: 0 }]);
            });

            if (routerConnections.length > 0) {
                connections[router.nodeId] = { main: routerConnections };
            }
        });

        // 3.4 ACTIONS → CONVERGENCE VERS OUTPUT (éviter les boucles)
        const output = nodeCategories.outputs[0];

        if (output) {
            // Toutes les actions convergent vers l'output
            nodeCategories.actions.forEach(action => {
                connections[action.nodeId] = {
                    main: [[{ node: output.nodeId, type: "main", index: 0 }]]
                };
            });
        } else {
            // Si pas d'output, chaîner les actions séquentiellement
            nodeCategories.actions.forEach((action, index) => {
                const nextAction = nodeCategories.actions[index + 1];
                if (nextAction) {
                    connections[action.nodeId] = {
                        main: [[{ node: nextAction.nodeId, type: "main", index: 0 }]]
                    };
                }
            });
        }

        // ═══════════════════════════════════════════════════════════════
        // SECTION 4: VALIDATION ET RETOUR DÉTAILLÉ
        // ═══════════════════════════════════════════════════════════════
        return {
            connections: connections,
            nodesFound: allNodes.length,
            connectionsCreated: Object.keys(connections).length,
            nodeCategories: {
                triggers: nodeCategories.triggers.length,
                processors: nodeCategories.processors.length,
                routers: nodeCategories.routers.length,
                actions: nodeCategories.actions.length,
                outputs: nodeCategories.outputs.length
            },
            nodeDetails: allNodes.map(n => ({ 
                id: n.nodeId, 
                originalId: n.originalId,
                type: n.nodeType, 
                name: n.nodeName,
                signature: n.signature
