                switch_node: nodeCategories.routers[0]?.nodeId || 'none',
                exit_point: nodeCategories.outputs[0]?.nodeId || 'none'
            }
        };

    } catch (error) {
        throw new Error(`Erreur création connexions: ${error.message}`);
    }
}

