// Extract area/location from email body
const emailBody = $input.first().json.text || $input.first().json.html;
const lines = emailBody.split('\n');

let area = '';
let city = '';
let state = '';
let zipcode = '';

// Look for area information in email
for (const line of lines) {
  if (line.toLowerCase().includes('area:') || line.toLowerCase().includes('location:')) {
    area = line.split(':')[1]?.trim() || '';
  }
  if (line.toLowerCase().includes('city:')) {
    city = line.split(':')[1]?.trim() || '';
  }
  if (line.toLowerCase().includes('state:')) {
    state = line.split(':')[1]?.trim() || '';
  }
  if (line.toLowerCase().includes('zip:') || line.toLowerCase().includes('zipcode:')) {
    zipcode = line.split(':')[1]?.trim() || '';
  }
}

// If no structured data found, try to extract from general text
if (!area && !city) {
  const addressRegex = /([A-Za-z\s]+),\s*([A-Z]{2})\s*(\d{5})?/;
  const match = emailBody.match(addressRegex);
  if (match) {
    city = match[1];
    state = match[2];
    zipcode = match[3] || '';
  }
}

return {
  json: {
    searchArea: area || city,
    city: city,
    state: state,
    zipcode: zipcode,
    originalEmail: $input.first().json,
    searchQuery: `${area || city} ${state} construction permits`.trim()
  }
};

const governmentData = $input.all()[0]?.json?.results || [];
const constructionData = $input.all()[1]?.json?.projects || [];
const searchInfo = $('Extract Location Info').first().json;

// Process government construction data
const govProjects = governmentData.map(item => ({
  title: item.position_title || 'Government Construction Project',
  location: item.locations?.[0] || searchInfo.searchArea,
  description: item.job_summary || 'No description available',
  source: 'Government Database',
  url: item.url || '#',
  startDate: item.start_date || 'TBD',
  type: 'Public Project'
}));

// Process construction industry data (fallback mock data if API fails)
let constructionProjects = [];
if (constructionData.length > 0) {
  constructionProjects = constructionData.map(item => ({
    title: item.name || item.title || 'Construction Project',
    location: item.location || searchInfo.searchArea,
    description: item.description || 'Commercial construction project',
    source: 'Construction Industry',
    url: item.url || '#',
    startDate: item.start_date || 'Q2 2024',
    type: item.type || 'Private Project'
  }));
} else {
  // Fallback mock data for demo purposes
  constructionProjects = [
    {
      title: `New Commercial Complex - ${searchInfo.searchArea}`,
      location: searchInfo.searchArea,
      description: 'Mixed-use commercial and residential development',
      source: 'Local Planning Department',
      url: '#',
      startDate: 'March 2024',
      type: 'Mixed Development'
    },
    {
      title: `Office Building Construction - ${searchInfo.city}`,
      location: `${searchInfo.city}, ${searchInfo.state}`,
      description: '5-story office building with retail space',
      source: 'Building Permits',
      url: '#',
      startDate: 'April 2024',
      type: 'Commercial'
    }
  ];
}

// Combine all projects
const allProjects = [...govProjects, ...constructionProjects];

// Filter for recent/upcoming projects
const recentProjects = allProjects.filter(project => {
  const startDate = new Date(project.startDate);
  const now = new Date();
  const threeMonthsFromNow = new Date(now.getTime() + (90 * 24 * 60 * 60 * 1000));

  return startDate >= now || project.startDate.includes('2024') || project.startDate === 'TBD';
});

return {
  json: {
    searchArea: searchInfo.searchArea,
    totalProjects: recentProjects.length,
    projects: recentProjects.slice(0, 10), // Limit to top 10
    searchQuery: searchInfo.searchQuery,
    generatedAt: new Date().toISOString(),
    originalEmail: searchInfo.originalEmail
  }
};

const data = $input.first().json;
const projects = data.projects;

// Create HTML email content
let htmlContent = `
<html>
<head>
  <style>
    body { font-family: Arial, sans-serif; margin: 20px; }
    .header { background-color: #f4f4f4; padding: 20px; border-radius: 5px; }
    .project { border: 1px solid #ddd; margin: 10px 0; padding: 15px; border-radius: 5px; }
    .project-title { color: #333; font-weight: bold; font-size: 16px; }
    .project-meta { color: #666; font-size: 12px; margin: 5px 0; }
    .project-desc { margin: 10px 0; }
    .summary { background-color: #e8f4fd; padding: 15px; border-radius: 5px; margin: 20px 0; }
  </style>
</head>
<body>
  <div class="header">
    <h2>üèóÔ∏è Construction Project Alert Report</h2>
    <p><strong>Search Area:</strong> ${data.searchArea}</p>
    <p><strong>Report Generated:</strong> ${new Date(data.generatedAt).toLocaleString()}</p>
  </div>

  <div class="summary">
    <h3>üìä Summary</h3>
    <p><strong>Total Projects Found:</strong> ${data.totalProjects}</p>
    <p><strong>Search Query:</strong> ${data.searchQuery}</p>
  </div>

  <h3>üîç Upcoming Construction Projects</h3>
`;

if (projects.length === 0) {
  htmlContent += '<p>No upcoming construction projects found in the specified area.</p>';
} else {
  projects.forEach((project, index) => {
    htmlContent += `
    <div class="project">
      <div class="project-title">${project.title}</div>
      <div class="project-meta">
        üìç Location: ${project.location} | 
        üìÖ Start Date: ${project.startDate} | 
        üè¢ Type: ${project.type}
      </div>
      <div class="project-desc">
        <strong>Description:</strong> ${project.description}
      </div>
      <div class="project-meta">
        <strong>Source:</strong> ${project.source}
      </div>
    </div>
    `;
  });
}

htmlContent += `
  <div style="margin-top: 30px; padding: 15px; background-color: #f9f9f9; border-radius: 5px;">
    <h4>üí° Next Steps</h4>
    <ul>
      <li>Review each project for potential competition</li>
      <li>Contact project owners for partnership opportunities</li>
      <li>Monitor progress and timeline changes</li>
      <li>Update your competitive analysis</li>
    </ul>
  </div>

  <p style="margin-top: 30px; color: #666; font-size: 12px;">
    This report was automatically generated by your Construction Alert System.<br>
    To modify your alert preferences, reply to this email with your requirements.
  </p>
</body>
</html>
`;

// Create plain text version
let textContent = `Construction Project Alert Report\n\n`;
textContent += `Search Area: ${data.searchArea}\n`;
textContent += `Total Projects Found: ${data.totalProjects}\n`;
textContent += `Report Generated: ${new Date(data.generatedAt).toLocaleString()}\n\n`;

if (projects.length === 0) {
  textContent += 'No upcoming construction projects found in the specified area.\n';
} else {
  textContent += 'UPCOMING CONSTRUCTION PROJECTS:\n';
  textContent += '=' .repeat(40) + '\n\n';

  projects.forEach((project, index) => {
    textContent += `${index + 1}. ${project.title}\n`;
    textContent += `   Location: ${project.location}\n`;
    textContent += `   Start Date: ${project.startDate}\n`;
    textContent += `   Type: ${project.type}\n`;
    textContent += `   Description: ${project.description}\n`;
    textContent += `   Source: ${project.source}\n\n`;
  });
}

return {
  json: {
    subject: `üèóÔ∏è Construction Alert: ${data.totalProjects} Projects Found in ${data.searchArea}`,
    htmlContent: htmlContent,
    textContent: textContent,
    recipientEmail: data.originalEmail.from,
    searchArea: data.searchArea,
    projectCount: data.totalProjects
  }
};

