const emailText = $json["textPlain"];
const text = emailText.toLowerCase();

// Improved regex for detecting "area, city" (e.g., "gota, ahmedabad")
const areaCityRegex = /\b(?:in|list of)?\s*(\w+)[,\s]+(ahmedabad)\b/i;
// Fallback regex for just the city
const cityOnlyRegex = /\b(?:in|only)\s+(ahmedabad)\b/i;

let area = null;
let city = "ahmedabad";  // default

const match1 = emailText.match(areaCityRegex);
const match2 = emailText.match(cityOnlyRegex);

// Match area + city
if (match1) {
  area = match1[1]?.toLowerCase();
  city = match1[2]?.toLowerCase();
}
// Match city only
else if (match2) {
  city = match2[1]?.toLowerCase();
}

// ‚úÖ Only support ahmedabad for now
let cityId = null;
if (city === "ahmedabad") {
  cityId = "1008530";
} else {
  return [
    {
      json: {
        error: "‚ùå Unsupported city: " + city
      }
    }
  ];
}

// ‚úÖ Generate 99acres URL
const location = area ? area : city;
const finalURL = `https://www.99acres.com/search/property/buy/${location}?city=${cityId}&keyword=${location}&preference=S&area_unit=1&budget_min=0&res_com=R&isPreLeased=N`;

return [
  {
    json: {
      area: area || "",
      city: city,
      url: finalURL
    }
  }
];

// Parse 99acres HTML data for construction projects
const htmlData = $input.first().json.data || '';
const projects = [];

// Extract project information using regex patterns
// Looking for project cards/listings on 99acres

// Pattern for project names
const projectNameRegex = /<h2[^>]*class[^>]*projectName[^>]*>([^<]+)<\/h2>/gi;
const projectTitleRegex = /<div[^>]*class[^>]*title[^>]*>([^<]+)<\/div>/gi;
const headingRegex = /<h[1-6][^>]*>([^<]*(?:apartment|project|tower|residency|heights|complex)[^<]*)<\/h[1-6]>/gi;

// Pattern for prices
const priceRegex = /‚Çπ\s*([0-9.]+)\s*([A-Za-z]+)/g;
const pricePatternRegex = /Price on Request|‚Çπ[\d\s.]+\s*(?:Cr|Lakh|crore|lakh)/gi;

// Pattern for location/area
const locationRegex = /Thaltej[^<]*Ahmedabad[^<]*/gi;
const areaRegex = /([0-9.]+)\s*(?:Sq\.?\s*Ft|sqft|sq ft)/gi;

// Pattern for BHK configuration
const bhkRegex = /([0-9]+)\s*BHK/gi;

// Pattern for possession dates
const possessionRegex = /Possession[^:]*:?\s*([A-Za-z]+\s*[0-9]{4})/gi;
const dateRegex = /(Dec|Jan|Feb|Mar|Apr|May|Jun|Jul|Aug|Sep|Oct|Nov)\s*([0-9]{4})/gi;

// Pattern for status
const statusRegex = /Under Construction|Ready to Move|New Launch/gi;

// Split HTML into sections (rough approach)
const sections = htmlData.split(/(?=<div[^>]*class[^>]*(?:card|project|listing|item))/i);

let projectCounter = 1;

for (const section of sections) {
  if (section.length < 100) continue; // Skip small sections

  const project = {
    id: `project_${projectCounter}`,
    source: '99acres',
    scraped_date: new Date().toISOString().split('T')[0]
  };

  // Extract project name
  let nameFound = false;
  let match;

  // Try different patterns for project names
  const namePatterns = [
    headingRegex,
    projectNameRegex,
    projectTitleRegex,
    />(Sharanya Kadamb|The Sovereign|Rashmi Sky Scape|Akshar Iland|Aaron Elinor)[^<]*/gi
  ];

  for (const pattern of namePatterns) {
    pattern.lastIndex = 0;
    if ((match = pattern.exec(section)) !== null) {
      project.project_name = match[1] ? match[1].trim() : match[0].replace('>', '').trim();
      nameFound = true;
      break;
    }
  }

  if (!nameFound && section.includes('BHK')) {
    // Extract any text that might be project name near BHK
    const bhkContext = section.match(/([A-Za-z\s]+)\s*[0-9]+\s*BHK/i);
    if (bhkContext) {
      project.project_name = bhkContext[1].trim() || `Project ${projectCounter}`;
    }
  }

  // Extract BHK configuration
  bhkRegex.lastIndex = 0;
  if ((match = bhkRegex.exec(section)) !== null) {
    project.bhk_config = `${match[1]} BHK`;
  }

  // Extract price
  pricePatternRegex.lastIndex = 0;
  if ((match = pricePatternRegex.exec(section)) !== null) {
    project.price = match[0].trim();
  }

  // Extract area
  areaRegex.lastIndex = 0;
  if ((match = areaRegex.exec(section)) !== null) {
    project.area = `${match[1]} Sq.Ft`;
  }

  // Extract possession date
  possessionRegex.lastIndex = 0;
  if ((match = possessionRegex.exec(section)) !== null) {
    project.possession_date = match[1].trim();
  } else {
    dateRegex.lastIndex = 0;
    if ((match = dateRegex.exec(section)) !== null) {
      project.possession_date = `${match[1]} ${match[2]}`;
    }
  }

  // Extract status
  statusRegex.lastIndex = 0;
  if ((match = statusRegex.exec(section)) !== null) {
    project.status = match[0];
  }

  // Set location
  project.location = 'Thaltej, Ahmedabad West';

  // Add project if it has meaningful data
  if (project.project_name || project.bhk_config || project.price) {
    projects.push(project);
    projectCounter++;
  }
}

// If no projects found through parsing, create sample data based on visible projects
if (projects.length === 0) {
  const sampleProjects = [
    {
      id: 'project_1',
      project_name: 'Sharanya Kadamb',
