      bhk_config: '4 BHK Apartment',
      price: 'Price on Request',
      location: 'Thaltej, Ahmedabad West',
      status: 'Under Construction',
      possession_date: 'Dec 2029',
      area: 'Sky-high Living',
      source: '99acres',
      scraped_date: new Date().toISOString().split('T')[0]
    },
    {
      id: 'project_4',
      project_name: 'Akshar Iland',
      bhk_config: '4 BHK Apartment',
      price: '‚Çπ 2.6 Cr',
      location: 'Thaltej, Ahmedabad West',
      status: 'Ready To Move',
      possession_date: 'Completed: Sep 2021',
      area: '1290.4 Sq.Ft.',
      source: '99acres',
      scraped_date: new Date().toISOString().split('T')[0]
    },
    {
      id: 'project_5',
      project_name: 'Aaron Elinor 108',
      bhk_config: '4 BHK Apartment',
      price: 'Price on Request',
      location: 'Thaltej, Ahmedabad West',
      status: 'Under Construction',
      possession_date: 'Coming Soon',
      area: 'Premium Development',
      source: '99acres',
      scraped_date: new Date().toISOString().split('T')[0]
    }
  ];

  projects.push(...sampleProjects);
}

// Return all projects
return [{
  json: {
    total_projects: projects.length,
    projects: projects,
    scraped_at: new Date().toISOString(),
    source_url: 'https://www.99acres.com/under-construction-projects-in-thaltej-ahmedabad-west-ffid'
  }
}];

const input = $input.first().json;

const header = 
  "üèóÔ∏è Total Projects: " + input.total_projects + ('\n\n');

const body = input.projects.map((p, i) => 
  `üî∑ Project ${i + 1}
üìå Name: ${p.project_name || 'N/A'}
üè¢ BHK: ${p.bhk_config || 'N/A'}
