// Simplified Product Analyzer
const products = $input.first().json.result || $input.first().json || [];

function analyzeProducts(productList) {
  if (!Array.isArray(productList) || productList.length === 0) {
    return {
      error: 'No products found',
      timestamp: new Date().toISOString()
    };
  }

  const analysis = {
    summary: {
      total_products: productList.length,
      analyzed_at: new Date().toISOString()
    },
    pricing: {
      average: 0,
      min: 0,
      max: 0,
      distribution: {
        budget: 0,      // < $50
        mid_range: 0,   // $50-150
        premium: 0      // > $150
      }
    },
    ratings: {
      average: 0,
      excellent: 0,   // >= 4.5
      good: 0,        // 4.0-4.4
      fair: 0,        // 3.5-3.9
      poor: 0         // < 3.5
    },
    top_products: [],
    opportunities: [],
    insights: []
  };

  // Extract and clean price data
  const validProducts = productList.filter(p => p.price && p.rating).map(p => ({
    ...p,
    price_numeric: parseFloat(String(p.price).replace(/[$,]/g, '') || '0'),
    rating_numeric: parseFloat(p.rating || '0'),
    review_count_numeric: parseInt(String(p.review_count || '0').replace(/[,]/g, '') || '0')
  }));

  if (validProducts.length === 0) {
    return { ...analysis, error: 'No valid product data found' };
  }

  // Pricing analysis
  const prices = validProducts.map(p => p.price_numeric).filter(p => p > 0);
  if (prices.length > 0) {
    analysis.pricing.average = Math.round(prices.reduce((a, b) => a + b, 0) / prices.length);
    analysis.pricing.min = Math.min(...prices);
    analysis.pricing.max = Math.max(...prices);

    // Price distribution
    analysis.pricing.distribution.budget = prices.filter(p => p < 50).length;
    analysis.pricing.distribution.mid_range = prices.filter(p => p >= 50 && p <= 150).length;
    analysis.pricing.distribution.premium = prices.filter(p => p > 150).length;
  }

  // Rating analysis
  const ratings = validProducts.map(p => p.rating_numeric).filter(r => r > 0);
  if (ratings.length > 0) {
    analysis.ratings.average = Math.round(ratings.reduce((a, b) => a + b, 0) / ratings.length * 10) / 10;
    analysis.ratings.excellent = ratings.filter(r => r >= 4.5).length;
    analysis.ratings.good = ratings.filter(r => r >= 4.0 && r < 4.5).length;
    analysis.ratings.fair = ratings.filter(r => r >= 3.5 && r < 4.0).length;
    analysis.ratings.poor = ratings.filter(r => r < 3.5).length;
  }

  // Top products (by rating and review count)
  analysis.top_products = validProducts
    .filter(p => p.rating_numeric >= 4.0)
    .sort((a, b) => {
      const scoreA = a.rating_numeric * Math.log(a.review_count_numeric + 1);
      const scoreB = b.rating_numeric * Math.log(b.review_count_numeric + 1);
      return scoreB - scoreA;
    })
    .slice(0, 5)
    .map(p => ({
      title: p.title,
      price: p.price_numeric,
      rating: p.rating_numeric,
      reviews: p.review_count_numeric,
      asin: p.asin
    }));

  // Market opportunities
  const lowRatedCount = analysis.ratings.poor + analysis.ratings.fair;
  if (lowRatedCount > validProducts.length * 0.3) {
    analysis.opportunities.push({
      type: 'Quality Gap',
      description: `${lowRatedCount} products have ratings below 4.0`,
      potential: 'High'
    });
  }

  const highPriceCount = analysis.pricing.distribution.premium;
  if (highPriceCount > validProducts.length * 0.4) {
    analysis.opportunities.push({
      type: 'Price Gap',
      description: 'Market skews toward premium pricing',
      potential: 'Medium'
    });
  }

  // Market insights
  analysis.insights.push(`Average price point: $${analysis.pricing.average}`);
  analysis.insights.push(`${Math.round(analysis.ratings.excellent / validProducts.length * 100)}% of products have excellent ratings`);

  if (analysis.pricing.distribution.budget > analysis.pricing.distribution.premium) {
    analysis.insights.push('Budget-friendly market with value opportunities');
  } else if (analysis.pricing.distribution.premium > analysis.pricing.distribution.budget) {
    analysis.insights.push('Premium market with higher margin potential');
  }

  return analysis;
}

const result = analyzeProducts(products);
return [{ json: result }];

// Keyword and SEO Analyzer
const analysis = $input.first().json;

function extractKeywords(productAnalysis) {
  const keywords = {
    primary_keywords: [],
    opportunities: [],
    trends: [],
    recommendations: [],
    timestamp: new Date().toISOString()
  };

  // Extract keywords from top products
  if (productAnalysis.top_products && productAnalysis.top_products.length > 0) {
    const allTitles = productAnalysis.top_products.map(p => p.title || '').join(' ');

    // Simple keyword extraction
    const words = allTitles.toLowerCase()
      .replace(/[^a-z0-9\s]/g, ' ')
      .split(/\s+/)
      .filter(word => word.length > 3 && !isStopWord(word));

    // Count frequency
    const wordCount = {};
    words.forEach(word => {
      wordCount[word] = (wordCount[word] || 0) + 1;
    });

    // Get top keywords
    keywords.primary_keywords = Object.entries(wordCount)
      .filter(([word, count]) => count >= 2)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 10)
      .map(([word, count]) => ({
        keyword: word,
        frequency: count,
        potential: count > 3 ? 'High' : 'Medium'
      }));
  }

  // SEO opportunities based on market analysis
  if (productAnalysis.opportunities) {
    productAnalysis.opportunities.forEach(opp => {
      if (opp.type === 'Quality Gap') {
        keywords.opportunities.push({
          type: 'Quality Keywords',
          suggestion: 'Target quality-focused keywords like "premium", "durable", "high-quality"',
          reason: 'Market has quality gap opportunity'
        });
      }

      if (opp.type === 'Price Gap') {
        keywords.opportunities.push({
          type: 'Value Keywords',
          suggestion: 'Use value-oriented keywords like "affordable", "best value", "budget"',
          reason: 'Price-sensitive market opportunity'
        });
      }
    });
  }

  // Market trends
  if (productAnalysis.pricing) {
    const { budget, mid_range, premium } = productAnalysis.pricing.distribution;
    const total = budget + mid_range + premium;

    if (premium > total * 0.4) {
      keywords.trends.push({
        trend: 'Premium Focus',
        keywords: ['premium', 'luxury', 'high-end', 'professional'],
        market_share: Math.round(premium / total * 100) + '%'
      });
    }

    if (budget > total * 0.4) {
      keywords.trends.push({
        trend: 'Value Focus',
        keywords: ['affordable', 'budget', 'value', 'economical'],
        market_share: Math.round(budget / total * 100) + '%'
      });
    }
  }

  // Basic recommendations
  keywords.recommendations = [
    {
      action: 'Target Long-tail Keywords',
      description: 'Focus on 3-4 word specific phrases',
      priority: 'High'
    },
    {
      action: 'Monitor Competitor Keywords',
      description: 'Analyze top performer titles for keyword ideas',
      priority: 'Medium'
    },
    {
      action: 'Optimize for Voice Search',
      description: 'Include natural language phrases',
      priority: 'Low'
    }
  ];

  return keywords;
}

function isStopWord(word) {
  const stopWords = ['with', 'for', 'and', 'the', 'that', 'this', 'from', 'wireless', 'bluetooth'];
  return stopWords.includes(word);
}

const keywordAnalysis = extractKeywords(analysis);
return [{ json: keywordAnalysis }];

// Simple Pricing Strategy Generator
const productAnalysis = $input.item(0).json;
const keywordAnalysis = $input.item(1).json;

function generatePricingStrategy(products, keywords) {
  const strategy = {
    current_market: {
      average_price: products.pricing?.average || 0,
      price_range: {
        min: products.pricing?.min || 0,
        max: products.pricing?.max || 0
      },
      market_distribution: products.pricing?.distribution || {}
    },
    recommendations: [],
    competitive_analysis: {
      positioning_options: [],
      risk_assessment: 'Medium'
    },
    action_items: [],
    timestamp: new Date().toISOString()
  };

  const avgPrice = products.pricing?.average || 100;

  // Generate pricing recommendations
  strategy.recommendations = [
    {
      strategy: 'Competitive Pricing',
      price_range: {
        min: Math.round(avgPrice * 0.95),
        max: Math.round(avgPrice * 1.05)
      },
      description: 'Price within 5% of market average',
      pros: ['Market acceptance', 'Lower risk'],
      cons: ['Limited differentiation'],
      best_for: 'New market entrants'
    },
    {
      strategy: 'Value Pricing',
      price_range: {
        min: Math.round(avgPrice * 0.75),
        max: Math.round(avgPrice * 0.90)
      },
      description: 'Undercut market by 10-25%',
      pros: ['Higher volume potential', 'Market share gains'],
      cons: ['Lower margins', 'Price war risk'],
      best_for: 'Cost-optimized products'
    },
    {
      strategy: 'Premium Pricing',
      price_range: {
        min: Math.round(avgPrice * 1.15),
        max: Math.round(avgPrice * 1.35)
      },
      description: 'Price 15-35% above market',
      pros: ['Higher margins', 'Premium positioning'],
      cons: ['Lower volume', 'Higher marketing needs'],
      best_for: 'Superior quality products'
    }
  ];

  // Positioning analysis
  const distribution = products.pricing?.distribution || {};
  const total = (distribution.budget || 0) + (distribution.mid_range || 0) + (distribution.premium || 0);

  if (total > 0) {
    strategy.competitive_analysis.positioning_options = [
      {
        position: 'Budget Leader',
        market_share: Math.round((distribution.budget || 0) / total * 100) + '%',
        competition_level: distribution.budget > 5 ? 'High' : 'Medium',
        opportunity: distribution.budget < total * 0.3 ? 'High' : 'Low'
      },
      {
        position: 'Mid-Range Player',
        market_share: Math.round((distribution.mid_range || 0) / total * 100) + '%',
        competition_level: distribution.mid_range > 5 ? 'High' : 'Medium',
        opportunity: distribution.mid_range < total * 0.4 ? 'Medium' : 'Low'
      },
      {
        position: 'Premium Brand',
        market_share: Math.round((distribution.premium || 0) / total * 100) + '%',
        competition_level: distribution.premium > 3 ? 'High' : 'Low',
        opportunity: distribution.premium < total * 0.2 ? 'High' : 'Medium'
      }
    ];
  }

  // Risk assessment
  const qualityIssues = products.opportunities?.some(o => o.type === 'Quality Gap') || false;
  const priceCompetition = distribution.budget > distribution.premium;

  if (qualityIssues && priceCompetition) {
    strategy.competitive_analysis.risk_assessment = 'High';
  } else if (qualityIssues || priceCompetition) {
    strategy.competitive_analysis.risk_assessment = 'Medium';
  } else {
    strategy.competitive_analysis.risk_assessment = 'Low';
  }

  // Action items
  strategy.action_items = [
    {
      priority: 'High',
      action: 'Choose pricing strategy',
      timeline: '1-2 weeks',
      description: 'Select from competitive, value, or premium pricing'
    },
    {
      priority: 'High',
      action: 'Monitor competitor pricing',
      timeline: 'Ongoing',
      description: 'Track price changes of top 5 competitors weekly'
    },
    {
      priority: 'Medium',
      action: 'Test price sensitivity',
      timeline: '4-6 weeks',
      description: 'A/B test different price points'
    },
    {
      priority: 'Low',
      action: 'Optimize for keywords',
      timeline: '2-4 weeks',
      description: 'Use keyword insights for product positioning'
    }
  ];

  return strategy;
}

const pricingStrategy = generatePricingStrategy(productAnalysis, keywordAnalysis);
return [{ json: pricingStrategy }];

// Final Report Generator
const productAnalysis = $input.item(0).json;
const keywordAnalysis = $input.item(1).json;
const pricingStrategy = $input.item(2).json;

function generateFinalReport(products, keywords, pricing) {
  const report = {
    executive_summary: {
      market_overview: {
        total_products_analyzed: products.summary?.total_products || 0,
        average_market_price: products.pricing?.average || 0,
        average_rating: products.ratings?.average || 0,
        market_health: calculateMarketHealth(products)
      },
      key_insights: [],
      recommendations: {
        immediate: [],
        short_term: [],
        long_term: []
      }
    },
    detailed_analysis: {
      pricing_insights: pricing,
      keyword_opportunities: keywords,
      product_performance: products
    },
    action_plan: {
      phase_1: [],
      phase_2: [],
      phase_3: []
    },
    kpis_to_track: [],
    generated_at: new Date().toISOString()
  };

  // Generate key insights
  const insights = [];

  if (products.pricing?.average) {
    insights.push(`Market average price is $${products.pricing.average}`);
  }

  if (products.opportunities?.length > 0) {
    insights.push(`${products.opportunities.length} market opportunities identified`);
  }

  if (products.ratings?.excellent > products.ratings?.poor) {
    insights.push('Market prioritizes quality over price');
  } else if (products.ratings?.poor > products.ratings?.excellent) {
    insights.push('Quality gap presents opportunity for premium positioning');
  }

  const premiumShare = pricing.current_market?.market_distribution?.premium || 0;
  const budgetShare = pricing.current_market?.market_distribution?.budget || 0;

  if (premiumShare > budgetShare) {
    insights.push('Premium segment dominates - consider quality focus');
  } else if (budgetShare > premiumShare) {
    insights.push('Value segment is strong - cost optimization important');
  }

  report.executive_summary.key_insights = insights;

  // Generate recommendations by timeline
  report.executive_summary.recommendations.immediate = [
    'Select primary pricing strategy based on analysis',
    'Identify top 3 competitor products to monitor',
    'Choose initial keyword targets for product listing'
  ];

  report.executive_summary.recommendations.short_term = [
    'Optimize product features based on market gaps',
    'Implement competitor price monitoring',
    'Test different price points with small batches'
  ];

  report.executive_summary.recommendations.long_term = [
    'Build brand positioning based on market insights',
    'Develop product line extensions',
    'Consider market expansion opportunities'
  ];

  // Create phased action plan
  report.action_plan.phase_1 = [
    {
      task: 'Market Entry Strategy',
      timeline: '0-4 weeks',
      priority: 'Critical',
      description: 'Finalize pricing, positioning, and initial keyword strategy'
    },
    {
      task: 'Competitive Monitoring Setup',
      timeline: '1-2 weeks',
      priority: 'High',
      description: 'Establish weekly tracking of top 5 competitors'
    }
  ];

  report.action_plan.phase_2 = [
    {
      task: 'Product Optimization',
      timeline: '4-12 weeks',
      priority: 'High',
      description: 'Refine product based on market feedback and competitor analysis'
    },
    {
      task: 'Marketing Strategy',
      timeline: '6-10 weeks',
      priority: 'Medium',
      description: 'Develop marketing campaigns based on keyword insights'
    }
  ];

  report.action_plan.phase_3 = [
    {
      task: 'Market Expansion',
      timeline: '12+ weeks',
      priority: 'Medium',
      description: 'Consider additional products or market segments'
    },
    {
      task: 'Brand Building',
      timeline: '16+ weeks',
      priority: 'Low',
      description: 'Invest in brand development and customer loyalty'
    }
  ];

  // Define KPIs to track
  report.kpis_to_track = [
    {
      metric: 'Market Share',
      target: 'Top 10 in category',
      frequency: 'Monthly'
    },
    {
      metric: 'Price Competitiveness',
      target: 'Within 10% of optimal price point',
      frequency: 'Weekly'
    },
    {
      metric: 'Customer Satisfaction',
      target: '4.5+ star rating',
      frequency: 'Daily'
    },
    {
      metric: 'Keyword Rankings',
      target: 'Top 3 for primary keywords',
      frequency: 'Weekly'
    },
    {
      metric: 'Sales Velocity',
      target: 'Above category average',
      frequency: 'Daily'
    }
  ];

  return report;
}

function calculateMarketHealth(products) {
  let score = 0;
  let factors = 0;

  // Rating health
  if (products.ratings?.average >= 4.0) {
    score += 3;
  } else if (products.ratings?.average >= 3.5) {
    score += 2;
  } else {
    score += 1;
  }
  factors++;

  // Opportunity assessment
  const opportunityCount = products.opportunities?.length || 0;
  if (opportunityCount >= 2) {
    score += 3;
  } else if (opportunityCount >= 1) {
    score += 2;
  } else {
    score += 1;
  }
  factors++;

  // Market size
  const totalProducts = products.summary?.total_products || 0;
  if (totalProducts >= 20) {
    score += 3;
  } else if (totalProducts >= 10) {
    score += 2;
  } else {
    score += 1;
  }
  factors++;

  const avgScore = score / factors;

  if (avgScore >= 2.5) return 'Healthy';
  if (avgScore >= 2.0) return 'Moderate';
  return 'Challenging';
}

const finalReport = generateFinalReport(productAnalysis, keywordAnalysis, pricingStrategy);
return [{ json: finalReport }];
