// Deal Analysis Engine
const dealData = $input.first().json;

function analyzeDealData() {
  // Simulated deal analysis since scraping might fail
  const mockDeals = [
    { store: 'Amazon', discount: 30, category: 'electronics', title: 'Echo Dot Sale' },
    { store: 'Best Buy', discount: 25, category: 'electronics', title: 'Laptop Deals' },
    { store: 'Target', discount: 40, category: 'home', title: 'Home Decor Sale' },
    { store: 'Walmart', discount: 20, category: 'general', title: 'Weekly Specials' }
  ];

  const analysis = {
    summary: {
      total_deals: mockDeals.length,
      average_discount: Math.round(mockDeals.reduce((sum, deal) => sum + deal.discount, 0) / mockDeals.length),
      top_categories: ['electronics', 'home', 'general'],
      analyzed_at: new Date().toISOString()
    },
    competitive_intelligence: {
      threat_level: mockDeals.some(d => d.discount > 35) ? 'High' : 'Medium',
      top_discounts: mockDeals.sort((a, b) => b.discount - a.discount).slice(0, 3),
      market_activity: 'Active'
    },
    urgent_deals: mockDeals.filter(d => d.discount >= 30)
  };

  return analysis;
}

const result = analyzeDealData();
return [{ json: result }];

// Customer Segmentation
const dealAnalysis = $input.first().json;

function segmentCustomers() {
  // Simulated customer database
  const customers = [
    { id: 'C001', name: 'John Smith', email: 'john@example.com', value: 2500, segment: 'vip' },
    { id: 'C002', name: 'Sarah Johnson', email: 'sarah@example.com', value: 450, segment: 'bargain_hunter' },
    { id: 'C003', name: 'Mike Wilson', email: 'mike@example.com', value: 1200, segment: 'at_risk' },
    { id: 'C004', name: 'Emma Davis', email: 'emma@example.com', value: 180, segment: 'new_prospect' }
  ];

  const segments = {
    vip_customers: customers.filter(c => c.segment === 'vip'),
    bargain_hunters: customers.filter(c => c.segment === 'bargain_hunter'),
    at_risk: customers.filter(c => c.segment === 'at_risk'),
    new_prospects: customers.filter(c => c.segment === 'new_prospect')
  };

  return {
    segments,
    total_customers: customers.length,
    segmentation_date: new Date().toISOString()
  };
}

const segmentation = segmentCustomers();
return [{ json: segmentation }];

// Generate Personalized Offers
const dealAnalysis = $input.item(0).json;
const customerSegments = $input.item(1).json;

function generateOffers(deals, segments) {
  const avgDiscount = deals.summary.average_discount;
  const threatLevel = deals.competitive_intelligence.threat_level;

  const offers = {
    campaign_batches: [],
    offer_summary: {
      total_campaigns: 0,
      projected_revenue: 0,
      generated_at: new Date().toISOString()
    }
  };

  // Generate offers for each segment
  Object.keys(segments.segments).forEach(segmentName => {
    const segmentCustomers = segments.segments[segmentName];
    if (segmentCustomers.length === 0) return;

    let discountLevel;
    let priority;

    switch(segmentName) {
      case 'vip_customers':
        discountLevel = Math.max(15, avgDiscount - 5);
        priority = 'Medium';
        break;
      case 'bargain_hunters':
        discountLevel = Math.max(avgDiscount + 5, 35);
        priority = threatLevel === 'High' ? 'Critical' : 'High';
        break;
      case 'at_risk':
        discountLevel = Math.max(avgDiscount + 10, 40);
        priority = 'Critical';
        break;
      case 'new_prospects':
        discountLevel = Math.max(20, avgDiscount);
        priority = 'Low';
        break;
      default:
        discountLevel = avgDiscount;
        priority = 'Medium';
    }

    const batch = {
      segment: segmentName,
      customer_count: segmentCustomers.length,
      discount_percentage: discountLevel,
      priority: priority,
      estimated_revenue: segmentCustomers.length * 75, // $75 average per customer
      customers: segmentCustomers.map(customer => ({
        id: customer.id,
        email: customer.email,
        name: customer.name,
        discount_code: `DEAL${Math.random().toString(36).substr(2, 6).toUpperCase()}`,
        personalized_discount: discountLevel
      }))
    };

    offers.campaign_batches.push(batch);
  });

  offers.offer_summary.total_campaigns = offers.campaign_batches.length;
  offers.offer_summary.projected_revenue = offers.campaign_batches.reduce(
    (sum, batch) => sum + batch.estimated_revenue, 0
  );

  return offers;
}

const personalizedOffers = generateOffers(dealAnalysis, customerSegments);
return [{ json: personalizedOffers }];

// Email Campaign Generator
const offers = $input.first().json;

function createEmailCampaigns(offerData) {
  const campaigns = [];

  offerData.campaign_batches.forEach(batch => {
    batch.customers.forEach(customer => {
      const emailContent = {
        to: customer.email,
        subject: getSubjectLine(batch.segment, customer.personalized_discount),
        campaign_id: `CAMP_${batch.segment.toUpperCase()}_${Date.now()}`,
        customer_name: customer.name,
        discount_code: customer.discount_code,
        discount_percentage: customer.personalized_discount,
        priority: batch.priority,
        template: getEmailTemplate(batch.segment)
      };

      campaigns.push(emailContent);
    });
  });

  return {
    email_campaigns: campaigns,
    summary: {
      total_emails: campaigns.length,
      high_priority: campaigns.filter(c => c.priority === 'Critical').length,
      projected_revenue: offerData.offer_summary.projected_revenue,
      created_at: new Date().toISOString()
    }
  };
}

function getSubjectLine(segment, discount) {
  const subjects = {
    'vip_customers': `Exclusive ${discount}% Off - VIP Access Only`,
    'bargain_hunters': `Flash Sale: ${discount}% Off Everything!`,
    'at_risk': `We Miss You! ${discount}% Off to Come Back`,
    'new_prospects': `Welcome! ${discount}% Off Your First Order`
  };
  return subjects[segment] || `Special ${discount}% Discount Just for You`;
}

function getEmailTemplate(segment) {
  const templates = {
    'vip_customers': 'elegant_gold_template',
    'bargain_hunters': 'urgent_red_template',
    'at_risk': 'warm_comeback_template',
    'new_prospects': 'welcome_blue_template'
  };
  return templates[segment] || 'default_template';
}

const emailCampaigns = createEmailCampaigns(offers);
return [{ json: emailCampaigns }];

// Analytics Dashboard
const dealAnalysis = $input.item(0).json;
const customerSegments = $input.item(1).json;
const offers = $input.item(2).json;
const emailCampaigns = $input.item(3).json;

function generateReport(deals, segments, offerData, campaigns) {
  const report = {
    executive_summary: {
      date: new Date().toISOString().split('T')[0],
      total_deals_analyzed: deals.summary.total_deals,
      average_market_discount: deals.summary.average_discount,
      threat_level: deals.competitive_intelligence.threat_level,
      customers_targeted: segments.total_customers,
      campaigns_created: campaigns.summary.total_emails,
      projected_revenue: campaigns.summary.projected_revenue
    },
    key_insights: [
      `Market average discount: ${deals.summary.average_discount}%`,
      `Competitive threat level: ${deals.competitive_intelligence.threat_level}`,
      `${segments.total_customers} customers across ${Object.keys(segments.segments).length} segments`,
      `${campaigns.summary.high_priority} high-priority campaigns scheduled`
    ],
    immediate_actions: [],
    segment_performance: {},
    next_execution: new Date(Date.now() + 24*60*60*1000).toISOString()
  };

  // Generate immediate actions based on threat level
  if (deals.competitive_intelligence.threat_level === 'High') {
    report.immediate_actions.push('Deploy aggressive counter-pricing strategy');
    report.immediate_actions.push('Increase email send frequency');
  }

  // Segment performance breakdown
  offerData.campaign_batches.forEach(batch => {
    report.segment_performance[batch.segment] = {
      customers: batch.customer_count,
      discount_offered: batch.discount_percentage + '%',
      priority: batch.priority,
      estimated_revenue: batch.estimated_revenue
    };
  });

  return report;
}

const analyticsReport = generateReport(dealAnalysis, customerSegments, offers, emailCampaigns);
return [{ json: analyticsReport }];
