const references = [
  {
    title: "Framed Portrait in Home",
    description: "Framed portrait hanging in a cozy home interior, placed above a mantel or sofa.",
    themes: ["Framing", "Interior", "Wall Art"]
  },
  {
    title: "Framing Overlay",
    description: "A blurred frame photo with a text overlay that says 'custom framing.'",
    themes: ["Framing", "Product", "Behind the Scenes"]
  },
  {
    title: "Fatherâ€™s Day Collage",
    description: "A collage of 2â€“3 photos with 'Happy Fatherâ€™s Day' text on top.",
    themes: ["Fatherâ€™s Day", "Holiday"]
  },
  {
    title: "Testimonial Quote",
    description: "Blurred image with a client review placed in elegant text overlay.",
    themes: ["Testimonial", "Client Quote"]
  },
  {
    title: "Mac Behind the Scenes",
    description: "Photo of Mac working during a shoot â€” camera in hand, lights or backdrops visible.",
    themes: ["Artist Insight", "Behind the Scenes"]
  },
  {
    title: "Beach Portrait Styling",
    description: "Carousel of beach portraits with text like 'what to wear' or 'color palette tips.'",
    themes: ["Beach", "Style Guide", "Educational"]
  },
  {
    title: "Studio Portrait",
    description: "Neutral-tone studio portrait with soft lighting and minimal background.",
    themes: ["Studio", "Interior"]
  },
  {
    title: "Summer Promo",
    description: "Ad-style post with text like 'Celebrate Summer' or 'Now Booking Beach Sessions.'",
    themes: ["Seasonal", "Summer", "Ad"]
  },
  {
    title: "Holiday Message",
    description: "One image with a decorative border and greeting for July 4th, New Year, etc.",
    themes: ["Holiday", "Seasonal", "Card"]
  },
  {
    title: "Consultation Snapshot",
    description: "A client and photographer reviewing prints or digital proofs in the studio.",
    themes: ["Client Experience", "Behind the Scenes", "Studio"]
  },
  {
    title: "Archival Printing",
    description: "Close-up of the printer or archival paper roll in use, highlighting quality.",
    themes: ["Printing", "Product", "Behind the Scenes"]
  },
  {
    title: "Custom Albums",
    description: "Photo of a hands flipping through a custom photo album on a wood table.",
    themes: ["Albums", "Products", "Details"]
  },
  {
    title: "Wall Display Mockup",
    description: "Mockup of a wall gallery with 2â€“4 portrait prints in a clean, staged room.",
    themes: ["Interior", "Framing", "Gallery"]
  },
  {
    title: "Tools of the Trade",
    description: "Flatlay of cameras, lenses, and accessories on a studio desk.",
    themes: ["Artist Insight", "Tools", "Studio"]
  },
  {
    title: "Gift Card Promo",
    description: "Photo of a wrapped gift with an HP card insert, styled like a holiday present.",
    themes: ["Gifts", "Promo", "Product"]
  }
];

// Matching logic
const input = query.toLowerCase();

let match = references.find(ref =>
  ref.themes.some(theme => input.includes(theme.toLowerCase()))
);

if (!match) {
  match = references[Math.floor(Math.random() * references.length)];
}

return match.description;

const items = $input.all();
const output = [];

for (const item of items) {
  const metadata = item.json.metadata || {};
  const url = metadata.canonicalUrl || item.json.url || '';
  const title = metadata.title || '';
  const description = metadata.description || '';

  // Optional: Filter out any posts without a title
  if (title && url) {
    output.push({
      json: {
        URL: url,
        Title: title,
        Description: description
      }
    });
  }
}

return output;

const startDate = new Date("2025-07-07");
const endDate = new Date("2026-01-30");

const daysOfWeek = [1, 3, 5]; // Monday, Wednesday, Friday
const fallbackPillars = ["Family Images", "Behind-the-Scenes"];
const serviceSubThemes = [
  "Beach", "Park", "Family", "Maternity", "Newborns",
  "Headshots", "Studio", "Beauty", "Frames",
  "Photo albums", "Cards", "Birthdays", "Anniversaries"
];
const processSubThemes = [
  "Images of studio layout/exterior of studio",
  "Printer + archival inks",
  "Frame options/process",
  "Photo of camera/lighting equipment used to prove quality",
  "Photo presentation and selection",
  "Photo of consultation (blurred client + focus on HP employee)"
];

const items = $input.all();
const contentRows = items.filter(r => r.json.Pillar);
const blogPosts = items.filter(r => r.json.URL);

const output = [];
const lastUsedDates = {};
const serviceUsedMonths = {};
const processUsedMonths = {};
let serviceSubThemeIndex = 0;
let processSubThemeIndex = 0;
let lastFallbackUsed = null;

function getSeasonStage(date) {
  const month = date.getMonth() + 1; // JavaScript months are 0-based

  if (month === 12 || month === 1 || month === 2) {
    if (month === 12) return "Early Winter";
    if (month === 1) return "Mid Winter";
    return "Late Winter"; // February
  }
  if (month === 3 || month === 4 || month === 5) {
    if (month === 3) return "Early Spring";
    if (month === 4) return "Mid Spring";
    return "Late Spring"; // May
  }
  if (month === 6 || month === 7 || month === 8) {
    if (month === 6) return "Early Summer";
    if (month === 7) return "Mid Summer";
    return "Late Summer"; // August
  }
  if (month === 9 || month === 10 || month === 11) {
    if (month === 9) return "Early Fall";
    if (month === 10) return "Mid Fall";
    return "Late Fall"; // November
  }
}


function getMinDays(freq) {
  const f = (freq || '').toLowerCase();
  if (f.includes('bi-weekly')) return 14;
  if (f.includes('weekly')) return 7;
  if (f.includes('daily')) return 1;
  if (f.includes('every 2 months')) return 60;
  if (f.includes('monthly') || f.includes('every 4 weeks')) return 30;
  return 14;
}

function daysBetween(d1, d2) {
  return Math.floor((d2 - d1) / (1000 * 60 * 60 * 24));
}

function isScheduledDay(date) {
  return daysOfWeek.includes(date.getDay());
}

for (let d = new Date(startDate); d <= endDate; d.setDate(d.getDate() + 1)) {
  if (!isScheduledDay(d)) continue;

  const date = new Date(d);
  const dateStr = date.toISOString().split('T')[0];
  const dayName = date.toLocaleDateString('en-US', { weekday: 'long' });
  const seasonStage = getSeasonStage(date);
  const monthKey = `${date.getFullYear()}-${date.getMonth()}`;
  const currentMonthName = date.toLocaleString('default', { month: 'long' }).toLowerCase();
  let scheduled = false;

  // Blog logic
  const blog = blogPosts.find(b => {
    const preferredMonth = (b.json["Preferred Month"] || '').toLowerCase();
    return preferredMonth === currentMonthName && !b.json._used;
  });

  if (blog) {
    blog.json._used = true;
    output.push({
      json: {
        Date: dateStr,
        Day: dayName,
        Pillar: "Posts based on blogs",
        Format: "Images",
        Description: blog.json.Description || '',
        Examples: `${blog.json.Title} â€” ${blog.json.URL}`,
        SeasonStage: seasonStage
      }
    });
    continue;
  }

  // Regular content
  for (const row of contentRows) {
    const basePillar = row.json.Pillar.trim();
    const freq = row.json.Frequency;
    const minGap = getMinDays(freq);
    const lastUsed = lastUsedDates[basePillar];
    const diff = lastUsed ? daysBetween(lastUsed, date) : Infinity;

    if (diff < minGap) continue;
    if (output.length > 0 && output[output.length - 1].json.Pillar === basePillar) continue;

    const post = JSON.parse(JSON.stringify(row.json)); // Deep copy
    let finalPillar = basePillar;

    if (basePillar.toLowerCase() === "types of services offered") {
      if (serviceUsedMonths[monthKey]) continue;
      const sub = serviceSubThemes[serviceSubThemeIndex % serviceSubThemes.length];
      finalPillar = `Types of services offered â€“ ${sub}`;
      serviceUsedMonths[monthKey] = true;
      serviceSubThemeIndex++;
    }

    if (basePillar.toLowerCase() === "explanation of processes (ex: presentation, printing, installation)") {
      if (processUsedMonths[monthKey]) continue;
      const sub = processSubThemes[processSubThemeIndex % processSubThemes.length];
      finalPillar = `Explanation of processes â€“ ${sub}`;
      processUsedMonths[monthKey] = true;
      processSubThemeIndex++;
    }

    output.push({
      json: {
        Date: dateStr,
        Day: dayName,
        Pillar: finalPillar,
        Format: post["Content Form"] || post["Format"] || '',
        Description: post["Structure"] || '',
        Examples: post["Examples"] || '',
        SeasonStage: seasonStage
      }
    });
    lastUsedDates[basePillar] = date;
    scheduled = true;
    break;
  }

  // Fallback logic
  if (!scheduled) {
    const options = fallbackPillars.filter(p => p !== lastFallbackUsed);
    const fallback = options[0] || fallbackPillars[0];
    output.push({
      json: {
        Date: dateStr,
        Day: dayName,
        Pillar: fallback,
        Format: '',
        Description: '',
        Examples: '',
        SeasonStage: seasonStage
      }
    });
    lastFallbackUsed = fallback;
  }
}

return output;

const post = $json;
const date = new Date(post.Date);
const year = date.getFullYear();
const month = date.getMonth(); // 0 = Jan, 11 = Dec
const dateNum = date.getDate();
const day = date.getDay(); // 0 = Sun, 1 = Mon, ..., 6 = Sat

const formattedDate = date.toISOString().split("T")[0];

function isNthWeekday(month, weekday, n) {
  let count = 0;
  for (let i = 1; i <= 31; i++) {
    const d = new Date(year, month, i);
    if (d.getMonth() !== month) break;
    if (d.getDay() === weekday) count++;
    if (count === n) return i;
  }
  return -1;
}

function isLastWeekday(month, weekday) {
  let last = -1;
  for (let i = 1; i <= 31; i++) {
    const d = new Date(year, month, i);
    if (d.getMonth() !== month) break;
    if (d.getDay() === weekday) last = i;
  }
  return last;
}

// Easter (hardcoded for accuracy)
const easterDates = {
  2025: "2025-04-20",
  2026: "2026-04-05",
  2027: "2027-03-28"
};

let holiday = "None";

// Fixed-date holidays
if (month === 0 && dateNum === 1) holiday = "New Yearâ€™s Day";
else if (month === 1 && dateNum === 14) holiday = "Valentineâ€™s Day";
else if (month === 2 && dateNum === 17) holiday = "St. Patrickâ€™s Day";
else if (formattedDate === easterDates[year]) holiday = "Easter";
else if (month === 5 && dateNum === 19) holiday = "Juneteenth";
else if (month === 6 && dateNum === 4) holiday = "Independence Day";
else if (month === 9 && dateNum === 11) holiday = "Veterans Day";
else if (month === 9 && dateNum === 31) holiday = "Halloween"; // rare edge case
else if (month === 11 && dateNum === 25) holiday = "Christmas Day";

// Floating holidays
else if (month === 0 && dateNum === isNthWeekday(0, 1, 3)) holiday = "Martin Luther King Jr. Day";
else if (month === 1 && dateNum === isNthWeekday(1, 1, 3)) holiday = "Presidentsâ€™ Day";
else if (month === 4 && dateNum === isNthWeekday(4, 0, 2)) holiday = "Motherâ€™s Day";
else if (month === 4 && dateNum === isLastWeekday(4, 1)) holiday = "Memorial Day";
else if (month === 5 && dateNum === isNthWeekday(5, 0, 3)) holiday = "Fatherâ€™s Day";
else if (month === 8 && dateNum === isNthWeekday(8, 1, 1)) holiday = "Labor Day";
else if (month === 9 && dateNum === isNthWeekday(9, 1, 2)) holiday = "Columbus Day";
else if (month === 10 && dateNum === isNthWeekday(10, 4, 4)) holiday = "Thanksgiving";

post.Holiday = holiday;
return { json: post };

const fullText = $json.output || '';
const lines = fullText.split('\n');

let caption = '';
let visualDescription = '';
let hashtags = '';

let currentSection = '';

for (const line of lines) {
  const trimmed = line.trim();

  // Normalize section header detection (handles : â€” or none)
  if (/^\*?\*?\s*Caption\b/i.test(trimmed)) {
    currentSection = 'caption';
    continue;
  } else if (/^\*?\*?\s*Visual Description\b/i.test(trimmed)) {
    currentSection = 'visualDescription';
    continue;
  } else if (/^\*?\*?\s*Hashtags\b/i.test(trimmed)) {
    currentSection = 'hashtags';
    continue;
  }

  if (currentSection === 'caption') caption += trimmed + ' ';
  else if (currentSection === 'visualDescription') visualDescription += trimmed + ' ';
  else if (currentSection === 'hashtags') hashtags += trimmed + ' ';
}

return {
  json: {
    Caption: caption.trim(),
    Description: visualDescription.trim(),
    Hashtags: hashtags.trim()
  }
};
