  return {
    json: {
      ...($('Webhook Arrs custom list JustWatch').first().json.params.objectType === "MOVIE" && {
        id: Number(node.content.externalIds.tmdbId)
      }),
      imdbId: node.content.externalIds.imdbId
    }
  };
});

parameters.jsonBody:
={
    "operationName": "GetPopularTitles",
    "variables": {
        "first": {{ $json.params.limit }},
        "popularTitlesSortBy": "POPULAR",
        "sortRandomSeed": 0,
        "offset": null,
        "creditsRole": "DIRECTOR",
        "after": "",
        "popularTitlesFilter": {
            "ageCertifications": [],
            "excludeGenres": [],
            "excludeProductionCountries": [],
            "objectTypes": [
                "{{ $json.params.objectType }}"
            ],
            "productionCountries": [],
            "subgenres": [],
            "genres": [],
            "packages": [
                "{{ $json.params.filter }}"
            ],
            "excludeIrrelevantTitles": false,
            "presentationTypes": [],
            "monetizationTypes": [],
            "searchQuery": ""
        },
        "watchNowFilter": {
            "packages": [
                "{{ $json.params.filter }}"
            ],
            "monetizationTypes": []
        },
        "language": "en",
        "country": "NL"
    },
    "query": "query GetPopularTitles($backdropProfile: BackdropProfile, $country: Country!, $first: Int! = 70, $format: ImageFormat, $language: Language!, $after: String, $popularTitlesFilter: TitleFilter, $popularTitlesSortBy: PopularTitlesSorting! = POPULAR, $profile: PosterProfile, $sortRandomSeed: Int! = 0, $watchNowFilter: WatchNowOfferFilter!, $offset: Int = 0, $creditsRole: CreditRole! = DIRECTOR) {
  popularTitles(
    country: $country
    filter: $popularTitlesFilter
    first: $first
    sortBy: $popularTitlesSortBy
    sortRandomSeed: $sortRandomSeed
    offset: $offset
    after: $after
  ) {
    __typename
    edges {
      cursor
      node {
        ...PopularTitleGraphql
        __typename
      }
      __typename
    }
    pageInfo {
      startCursor
      endCursor
      hasPreviousPage
      hasNextPage
      __typename
    }
    totalCount
  }
}

fragment PopularTitleGraphql on MovieOrShow {
  __typename
  id
  objectId
  objectType
  content(country: $country, language: $language) {
      externalIds {
        tmdbId
    imdbId
      }
   title
    fullPath
    originalReleaseYear
    shortDescription
    interactions {
      likelistAdditions
      dislikelistAdditions
      __typename
    }
    scoring {
      imdbVotes
      imdbScore
      tmdbPopularity
      tmdbScore
      tomatoMeter
      certifiedFresh
      jwRating
      __typename
    }
    interactions {
      votesNumber
      __typename
    }
    dailymotionClips: clips(providers: [DAILYMOTION]) {
      sourceUrl
      externalId
      provider
      streamUrl
      __typename
    }
    posterUrl(profile: $profile, format: $format)
    ... on MovieOrShowOrSeasonContent {
      backdrops(profile: $backdropProfile, format: $format) {
        backdropUrl
        __typename
      }
      __typename
    }
    isReleased
    credits(role: $creditsRole) {
      name
      personId
      __typename
    }
    runtime
    genres {
      translation(language: $language)
      shortName
      __typename
    }
    __typename
  }
  likelistEntry {
    createdAt
    __typename
  }
  dislikelistEntry {
    createdAt
    __typename
  }
  watchlistEntryV2 {
    createdAt
    __typename
  }
  customlistEntries {
    createdAt
    __typename
  }
  freeOffersCount: offerCount(
    country: $country
    platform: WEB
    filter: {monetizationTypes: [FREE, ADS]}
  )
  watchNowOffer(country: $country, platform: WEB, filter: $watchNowFilter) {
    ...WatchNowOffer
    __typename
  }
  ... on Movie {
    seenlistEntry {
      createdAt
      __typename
    }
    __typename
  }
  ... on Show {
    tvShowTrackingEntry {
      createdAt
      __typename
    }
    seenState(country: $country) {
      seenEpisodeCount
      progress
      __typename
    }
    __typename
  }
}

fragment WatchNowOffer on Offer {
  __typename
  id
  standardWebURL
  preAffiliatedStandardWebURL
  streamUrl
  package {
    id
    icon
    packageId
    clearName
    shortName
    technicalName
    iconWide(profile: S160)
    hasRectangularIcon(country: $country, platform: WEB)
    __typename
  }
  retailPrice(language: $language)
  retailPriceValue
  lastChangeRetailPriceValue
  currency
  presentationType
  monetizationType
  availableTo
  dateCreated
  newElementCount
}
"
}

