const searchEmail = $('Webhook Tautulli').first().json.body.user_email;
const users = $json["results"] || [];
const match = users.find(u => u.email.toLowerCase() === searchEmail.toLowerCase());
if (match) {
  return [{ json: match }];
} else {
  return [];
}

const targetTmdbId = $('Webhook Tautulli').first().json.body.media.ids.tmdb;
const targetTvdbId = $('Webhook Tautulli').first().json.body.media.ids.tvdb;

const requests = $json.results || [];

const match = requests.find(r => {
  const media = r.media || {};

  const tmdb = media.tmdbId;
  const tvdb = media.tvdbId;

  // Altijd als string vergelijken om typeverschillen te voorkomen
  const tmdbHit = tmdb != null && String(tmdb) === String(targetTmdbId);
  const tvdbHit = tvdb != null && String(tvdb) === String(targetTvdbId);

  return tmdbHit || tvdbHit;
});

if (match) {
  return [{ json: match }];
}

return [];

const targetFile = $('Webhook Arrs Dummy file update').first().json.body.deletedFiles[0].path;

// Haal de JSON op van de vorige stap
const data = items[0].json; // in n8n is dit meestal de response al als object

// Als jouw HTTP Request node een string geeft, gebruik dan:
// const data = JSON.parse(items[0].json);

// Pak de sessies array
const sessions = data?.response?.data?.sessions || [];
console.log(sessions)

// Filter de sessies
const matching = sessions.filter(s => s.file === targetFile);

// n8n wil dat je een array van items teruggeeft
return matching.map(s => ({ json: s }));

