// 1. Convert the incoming n8n items to a plain array
const dataArray = items.map(item => item.json);
// 2. Initialize counters
let totalSent = 0;
let totalOpens = 0;
let totalReplies = 0;
let totalBounced = 0;
let totalUnsubscribed = 0;
let totalOpportunities = 0;
let totalOpportunityValue = 0;
// 3. Sum up the metrics from each campaign's analytics
for (const data of dataArray) {
  totalSent += data.emails_sent_count ?? 0;
  totalOpens += data.open_count ?? 0;
  totalReplies += data.reply_count ?? 0;
  totalBounced += data.bounced_count ?? 0;
  totalUnsubscribed += data.unsubscribed_count ?? 0;
  totalOpportunities += data.total_opportunities ?? 0;
  totalOpportunityValue += data.total_opportunity_value ?? 0;
}
// 4. Compute rates (avoid division by zero)
const openRate = totalSent ? (totalOpens / totalSent) * 100 : 0;
const replyRate = totalSent ? (totalReplies / totalSent) * 100 : 0;
const bounceRate = totalSent ? (totalBounced / totalSent) * 100 : 0;
const unsubRate = totalSent ? (totalUnsubscribed / totalSent) * 100 : 0;
// Format the opportunity value as currency
const formattedOpportunityValue = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD',
  minimumFractionDigits: 0,
  maximumFractionDigits: 0
}).format(totalOpportunityValue);
// 5. Return aggregated metrics
return [
  {
    json: {
      totalSent: totalSent,
      totalOpens: totalOpens,
      totalReplies: totalReplies,
      totalBounced: totalBounced,
      totalUnsubscribed: totalUnsubscribed,
      totalOpportunities: totalOpportunities,
      totalOpportunityValue: totalOpportunityValue,
      // Added formatted version
      formattedOpportunityValue: formattedOpportunityValue,

      openRate: parseFloat(openRate.toFixed(2)),
      replyRate: parseFloat(replyRate.toFixed(2)),
      bounceRate: parseFloat(bounceRate.toFixed(2)),
      unsubRate: parseFloat(unsubRate.toFixed(2)),

      openPercentage: openRate.toFixed(2) + '%',
      replyPercentage: replyRate.toFixed(2) + '%',
      bouncePercentage: bounceRate.toFixed(2) + '%',
      unsubPercentage: unsubRate.toFixed(2) + '%'
    }
  }
];

