// 1. Grab all incoming items
const items = $input.all();

// 2. Sort by your date field descending
items.sort((a, b) => {
  const da = new Date(a.json.Date).getTime();
  const db = new Date(b.json.Date).getTime();
  return db - da;  // newest first
});

// 3. Return sorted items
return items;
