// Get current date and time
const now = new Date();
const today = now.toISOString().split('T')[0];
const currentHour = now.getHours();

// Get all schedule data
const scheduleData = $input.all();

// Filter classes for today and upcoming reminders
const todayClasses = scheduleData.filter(item => {
  const classDate = item.json['Class Date'];
  const classTime = item.json['Class Time'];
  const reminderTime = item.json['Reminder Time (Hours)'] || 1;

  // Check if class is today
  if (classDate === today) {
    // Parse class time
    const [hours, minutes] = classTime.split(':');
    const classHour = parseInt(hours);

    // Check if we need to send reminder
    const hoursUntilClass = classHour - currentHour;

    // Send reminder if within reminder window
    if (hoursUntilClass > 0 && hoursUntilClass <= reminderTime) {
      return true;
    }
  }

  return false;
});

// Also get tomorrow's classes for evening prep
const tomorrow = new Date(now);
tomorrow.setDate(tomorrow.getDate() + 1);
const tomorrowDate = tomorrow.toISOString().split('T')[0];

const tomorrowClasses = scheduleData.filter(item => {
  return item.json['Class Date'] === tomorrowDate && currentHour >= 18; // After 6 PM
});

// Combine today's reminders and tomorrow's prep
const classesToProcess = [...todayClasses, ...tomorrowClasses];

return classesToProcess.map(item => ({
  json: {
    ...item.json,
    reminderType: todayClasses.includes(item) ? 'today' : 'tomorrow',
    currentTime: now.toISOString()
  }
}));

// Get class data and student contacts
const classData = $('Filter Today\'s Classes').all();
const studentContacts = $input.all();

// Create reminder messages for each class
const reminders = [];

classData.forEach(classItem => {
  const classInfo = classItem.json;

  // Find students enrolled in this class
  const enrolledStudents = studentContacts.filter(student => {
    const studentClasses = student.json['Enrolled Classes'] || '';
    return studentClasses.includes(classInfo['Class Name']);
  });

  // Create reminder for each enrolled student
  enrolledStudents.forEach(student => {
    const reminderMessage = classInfo.reminderType === 'today' 
      ? `üîî Class Reminder: "${classInfo['Class Name']}" starts at ${classInfo['Class Time']} today!\n\nüìç Location: ${classInfo['Location'] || 'TBD'}\nüë®‚Äçüè´ Instructor: ${classInfo['Instructor'] || 'TBD'}\nüìö Topic: ${classInfo['Topic'] || 'Regular class'}\n\n‚ö†Ô∏è Please arrive 5 minutes early. Don't forget your materials!`
      : `üìÖ Tomorrow's Schedule: You have "${classInfo['Class Name']}" at ${classInfo['Class Time']}\n\nüìç Location: ${classInfo['Location'] || 'TBD'}\nüë®‚Äçüè´ Instructor: ${classInfo['Instructor'] || 'TBD'}\nüìö Topic: ${classInfo['Topic'] || 'Regular class'}\n\nüí° Tip: Prepare your materials tonight!`;

    reminders.push({
      json: {
        studentName: `${student.json['First Name']} ${student.json['Last Name']}`,
        studentEmail: student.json['Email'],
        studentPhone: student.json['Phone'],
        className: classInfo['Class Name'],
        classTime: classInfo['Class Time'],
        classDate: classInfo['Class Date'],
        location: classInfo['Location'],
        instructor: classInfo['Instructor'],
        topic: classInfo['Topic'],
        reminderType: classInfo.reminderType,
        message: reminderMessage,
        preferredContact: student.json['Preferred Contact'] || 'email'
      }
    });
  });
});

return reminders;

// Prepare email data
const reminderData = $input.all();

const emailsToSend = reminderData.map(item => {
  const data = item.json;

  const subject = data.reminderType === 'today' 
    ? `üîî Class Reminder: ${data.className} Today`
    : `üìÖ Tomorrow's Class: ${data.className}`;

  const htmlBody = `
    <!DOCTYPE html>
    <html>
    <head>
        <style>
            body { font-family: Arial, sans-serif; color: #333; margin: 0; padding: 20px; }
            .container { max-width: 600px; margin: 0 auto; }
            .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 20px; border-radius: 8px 8px 0 0; text-align: center; }
            .content { background: #f9f9f9; padding: 25px; border-radius: 0 0 8px 8px; }
            .class-info { background: white; padding: 20px; border-radius: 8px; margin: 15px 0; border-left: 4px solid #667eea; }
            .info-row { margin: 10px 0; }
            .info-label { font-weight: bold; color: #667eea; }
            .footer { text-align: center; margin-top: 20px; color: #666; font-size: 12px; }
            .emoji { font-size: 18px; }
        </style>
    </head>
    <body>
        <div class="container">
            <div class="header">
                <h1><span class="emoji">${data.reminderType === 'today' ? 'üîî' : 'üìÖ'}</span> Class ${data.reminderType === 'today' ? 'Reminder' : 'Preview'}</h1>
            </div>
            <div class="content">
                <p>Hello ${data.studentName},</p>
                <p>${data.reminderType === 'today' ? 'Your class is starting soon!' : 'Here\'s your schedule for tomorrow:'}</p>

                <div class="class-info">
                    <div class="info-row"><span class="info-label">üìö Class:</span> ${data.className}</div>
                    <div class="info-row"><span class="info-label">üïê Time:</span> ${data.classTime}</div>
                    <div class="info-row"><span class="info-label">üìÖ Date:</span> ${data.classDate}</div>
                    <div class="info-row"><span class="info-label">üìç Location:</span> ${data.location || 'TBD'}</div>
                    <div class="info-row"><span class="info-label">üë®‚Äçüè´ Instructor:</span> ${data.instructor || 'TBD'}</div>
                    <div class="info-row"><span class="info-label">üìñ Topic:</span> ${data.topic || 'Regular class session'}</div>
                </div>

                ${data.reminderType === 'today' 
                  ? '<p><strong>‚ö†Ô∏è Please arrive 5 minutes early and don\'t forget your materials!</strong></p>'
                  : '<p><strong>üí° Tip: Prepare your materials tonight for a smooth start tomorrow!</strong></p>'
                }

                <p>Have a great class!</p>
            </div>
            <div class="footer">
                <p>This is an automated reminder from your class management system.</p>
            </div>
        </div>
    </body>
    </html>
  `;

  return {
    json: {
      to: data.studentEmail,
      subject: subject,
      body: data.message,
      html: htmlBody,
      studentName: data.studentName,
      className: data.className
    }
  };
});

return emailsToSend;

// Prepare SMS data
const reminderData = $input.all();

const smsToSend = reminderData.map(item => {
  const data = item.json;

  const smsMessage = data.reminderType === 'today'
    ? `üîî Class Reminder: "${data.className}" starts at ${data.classTime} today at ${data.location || 'TBD'}. Arrive 5 min early!`
    : `üìÖ Tomorrow: "${data.className}" at ${data.classTime} (${data.location || 'TBD'}). Prepare tonight!`;

  return {
    json: {
      to: data.studentPhone,
      message: smsMessage,
      studentName: data.studentName,
      className: data.className
    }
  };
});

return smsToSend;

// Get existing log data
const existingLogs = $input.all();

// Get sent reminders data
const sentEmails = $('Prepare Email Reminders').all() || [];
const sentSMS = $('Prepare SMS Reminders').all() || [];

// Create log entries for sent reminders
const newLogEntries = [];

// Log email reminders
sentEmails.forEach(email => {
  newLogEntries.push({
    'Log ID': 'LOG-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
    'Timestamp': new Date().toISOString(),
    'Student Name': email.json.studentName,
    'Class Name': email.json.className,
    'Contact Method': 'Email',
    'Contact Info': email.json.to,
    'Status': 'Sent',
    'Reminder Type': 'Class Reminder',
    'Message Preview': email.json.subject
  });
});

// Log SMS reminders
sentSMS.forEach(sms => {
  newLogEntries.push({
    'Log ID': 'LOG-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9),
    'Timestamp': new Date().toISOString(),
    'Student Name': sms.json.studentName,
    'Class Name': sms.json.className,
    'Contact Method': 'SMS',
    'Contact Info': sms.json.to,
    'Status': 'Sent',
    'Reminder Type': 'Class Reminder',
    'Message Preview': sms.json.message.substring(0, 50) + '...'
  });
});

// Combine existing logs with new entries
const allLogs = [...existingLogs, ...newLogEntries];

return allLogs.map(item => ({ json: item }));
