const results = $json.data.result || [];
const output = [];

for (const entry of results) {
  const stream = entry.stream;
  const logs = entry.values;

  for (const [timestampNs, logLine] of logs) {
    let extractedLog = null;

    // Try to extract `content:` from inside extracteddata="map[...]"
    const extractedMatch = logLine.match(/extracteddata=".*?content:(.*?)(?:\s\w+:|]")/s);
    if (extractedMatch && extractedMatch[1]) {
      extractedLog = extractedMatch[1]
        .replace(/\\"/g, '"')
        .replace(/\\\\/g, '\\')
        .trim();
    }

    // If not extracted, fall back to raw logLine if it looks usable
    if (!extractedLog && logLine.includes("level=")) {
      extractedLog = logLine.trim();
    }

    // Filter out unwanted log levels
    if (extractedLog && /level=(debug|info)/i.test(extractedLog)) {
      continue; // Skip this log
    }

    output.push({
      json: {
        log: extractedLog || null,
        pod: stream.pod || "unknown",
        node: stream.node || "unknown",
        namespace: stream.namespace || "unknown",
        container: stream.container || "unknown",
        timestamp: new Date(Number(timestampNs) / 1e6).toISOString()
      }
    });
  }
}

return output;

const seen = new Set();
const output = [];

function normalizeLog(log) {
  return log
    .replace(/ts=\S+/g, '')                   // Remove 'ts=...' timestamps
    .replace(/\d+(\.\d+)?/g, '#')             // Replace all numbers with #
    .replace(/'[^']*'/g, '\'X\'')             // Mask quoted strings
    .replace(/\"[^\"]*\"/g, '"X"')            // Mask quoted strings
    .toLowerCase()
    .trim();
}

for (const item of items) {
  const pod = item.json.pod || 'unknown';
  const log = item.json.log || '';

  const normalized = normalizeLog(log);
  const dedupKey = `${pod}::${normalized}`;

  if (!seen.has(dedupKey)) {
    seen.add(dedupKey);
    output.push(item);
  }
}

return output;
