// Set a fixed base date to start counting from (can be any date)
const baseDate = new Date("2025-01-01");

// Get today's date
const today = new Date();

// Calculate the total number of days passed since the base date
const diffInDays = Math.floor((today - baseDate) / (1000 * 60 * 60 * 24));

// Each "slot" lasts 3 days. Divide the days passed by 3, then get the current slot index (0 to 9)
const index = Math.floor(diffInDays / 3) % 10;

// Return the index in the required format (e.g., for n8n or similar workflow tools)
return [{ json: { index } }];
}


