// N8N Code Node: Verwerkt LLM output en assembleert de Mem0 payload.
// Deze code is ontworpen om de output van een AI-model te structureren voor Mem0.

// ===================================================================
// I. CONFIGURATION
// ===================================================================
const APP_ID = "vexa_baserow_project";
const AGENT_ID = "vexa_baserow_ingestor";
const AGENT_CONTACT_NAMES = [
    { name: "Stephan", keywords: ["stephan", "stephan koning"] },
    // Voeg hier andere agent namen toe
];

// ===================================================================
// II. HELPER FUNCTIONS
// ===================================================================

// Helper: Vertaalt een datumstring naar een Unix timestamp (seconden sinds de epoch).
function getUnixTimestamp(dateString) {
    if (!dateString) return null;
    try {
        const dateObj = new Date(dateString);
        if (isNaN(dateObj.getTime())) return null;
        return Math.floor(dateObj.getTime() / 1000);
    } catch (e) {
        return null;
    }
}

// Helper: Zoekt naar bekende agentnamen in de tekst.
function determineAgentContactPerson(text) {
    if (!text) return '';
    const searchableText = String(text).toLowerCase();
    for (const agent of AGENT_CONTACT_NAMES) {
        if (agent.keywords.some(keyword => searchableText.includes(keyword))) {
            return agent.name;
        }
    }
    return '';
}

// Helper: Escaped een string voor JSON-compatibiliteit.
function escapeJsonString(str) {
    if (typeof str !== 'string') return str;
    return JSON.stringify(str).slice(1, -1);
}

// Helper: Haalt een waarde op uit een object met een fallback voor als de key niet bestaat.
function getWithFallback(obj, key, fallback = null) {
    if (obj && obj.hasOwnProperty(key)) {
        return obj[key] !== null ? obj[key] : fallback;
    }
    return fallback;
}

// ===================================================================
// III. MAIN EXECUTION
// ===================================================================
const output_items = [];

// Dit script verwerkt een enkel LLM output-item dat als input wordt gegeven.
try {
    const llm_output_item = items[0]; // De LLM output is het eerste item
    const llm_output = llm_output_item.json.output || {};

    const meeting_summary = getWithFallback(llm_output, 'summary', "No summary provided.");
    const meeting_sentiment = getWithFallback(llm_output, 'overallSentiment', "Neutral");
    const meeting_red_flags = getWithFallback(llm_output, 'potential red flags', "None");
    const meeting_next_steps = getWithFallback(llm_output, 'nextSteps', "None");
    const social_chatter = getWithFallback(llm_output, 'socialChatter', "None");
    const client_id = getWithFallback(llm_output, 'user_id', "unknown_client@example.com");

    const agent_contact_person_name = determineAgentContactPerson(meeting_summary);

    // --- Samenstellen van de 'content' voor Mem0 ---
    const mem0_content_raw = `Klant had een vergadering met ${agent_contact_person_name || 'een agent'}.

Samenvatting: ${meeting_summary}
Volgende Stappen: ${meeting_next_steps}`;

    const mem0_content = escapeJsonString(mem0_content_raw);

    // --- Samenstellen van de 'metadata' ---
    const mem0_metadata = {
        "type": "meeting",
        "summary": escapeJsonString(meeting_summary),
        "sentiment": escapeJsonString(meeting_sentiment),
        "red_flags": escapeJsonString(meeting_red_flags),
        "next_steps": escapeJsonString(meeting_next_steps),
        "social_chatter": escapeJsonString(social_chatter),
        "agent_contact_person_name": escapeJsonString(agent_contact_person_name),
        "native_meeting_id": getWithFallback(llm_output, 'native_meeting_id', 'no_id')
    };

    const mem0_categories = ["meeting", "summary"];
    if (meeting_red_flags.toLowerCase() !== "none" && meeting_red_flags.length > 2) {
        mem0_categories.push("red_flag");
    }
    if (meeting_next_steps.toLowerCase() !== "none") {
        mem0_categories.push("action_items");
    }

    // --- Definitieve Mem0 Payload ---
    const mem0_payload = {
        "messages": [
            {
                "role": "user",
                "content": mem0_content
            }
        ],
        "user_id": client_id,
        "agent_id": AGENT_ID,
        "app_id": APP_ID,
        "run_id": `meeting_${client_id}_${Date.now()}`,
        "metadata": mem0_metadata,
        "infer": true,
        "categories": mem0_categories,
        "version": "v2",
        "output_format": "v1.1",
        "timestamp": getUnixTimestamp(new Date().toISOString())
    };

    output_items.push({"json": {"mem0Payload": mem0_payload}});

} catch (error) {
    console.error(`Fout bij verwerken van LLM output:`, error.message, error);
    output_items.push({"json": {"error": true, "message": String(error), "originalItem": items[0].json}});
}

return output_items;
