const notices = $('Read Notices').all();
const contacts = $('Read Stakeholder Contacts').all();

const processedNotices = [];

for (const notice of notices) {
  const noticeData = notice.json;

  const targetAudience = noticeData.targetAudience.toLowerCase();
  const relevantContacts = contacts.filter(contact => {
    const contactData = contact.json;

    if (targetAudience === 'all') return true;
    if (targetAudience === 'students' && contactData.type === 'Student') return true;
    if (targetAudience === 'parents' && contactData.type === 'Parent') return true;
    if (targetAudience === 'teachers' && contactData.type === 'Teacher') return true;
    if (targetAudience === 'staff' && contactData.type === 'Staff') return true;

    return false;
  });

  for (const contact of relevantContacts) {
    processedNotices.push({
      noticeId: noticeData.noticeId || 'NOTICE-' + Date.now(),
      title: noticeData.title,
      message: noticeData.message,
      priority: noticeData.priority || 'Medium',
      targetAudience: noticeData.targetAudience,
      contactName: contact.json.name,
      contactEmail: contact.json.email,
      contactPhone: contact.json.phone,
      contactType: contact.json.type,
      distributionDate: new Date().toISOString().split('T')[0],
      status: 'Ready for Distribution'
    });
  }
}

return processedNotices.map(item => ({ json: item }));

const noticeData = $input.first().json;

const emailSubject = `[${noticeData.priority} Priority] ${noticeData.title}`;
const emailBody = `Dear ${noticeData.contactName},

${noticeData.message}

---
Notice Details:
- Priority: ${noticeData.priority}
- Target Audience: ${noticeData.targetAudience}
- Distribution Date: ${noticeData.distributionDate}
- Notice ID: ${noticeData.noticeId}

For any questions or concerns, please contact the school administration.

Best regards,
School Administration Team`;

return [{
  json: {
    to: noticeData.contactEmail,
    subject: emailSubject,
    body: emailBody,
    recipientName: noticeData.contactName,
    noticeId: noticeData.noticeId,
    priority: noticeData.priority
  }
}];

