// This is the final, production-ready code. It fixes the "PAID" stamp logic.

const items = $items("Combine Invoice, Logo & Signature"); // Ensure this is the correct name of your Merge node

// Step 1: Correctly access the data
const invoiceData = items[0].json;
const logoJson = items[1].json;
const signatureJson = items[2].json;

// Step 2: Safety check
if (!logoJson || !logoJson.logodata || !signatureJson || !signatureJson.signaturedata) {
  throw new Error("Could not find 'logodata' or 'signaturedata' properties. Check the nodes providing the images.");
}

// Step 3: Create the full, correct Data URI for the images
const logoSrc = `data:image/png;base64,${logoJson.logodata}`;
const signatureSrc = `data:image/png;base64,${signatureJson.signaturedata}`;

// Step 4: Format dates professionally
function formatDate(dateString) {
  if (!dateString) return 'N/A';
  const options = { year: 'numeric', month: 'long', day: 'numeric' };
  return new Date(dateString + 'T00:00:00-00:00').toLocaleDateString('en-US', options);
}

// Step 5: Build the HTML for the line items
let lineItemsHtml = '';
for (const item of invoiceData.Line) {
  if (item.DetailType === 'SalesItemLineDetail') {
    // Added a subtle grey color for the description for a more refined look
    const description = item.Description ? `<br><span style="color: #718096; font-size: 0.85rem;">${item.Description}</span>` : '';
    lineItemsHtml += `
      <tr>
        <td><strong>${item.SalesItemLineDetail.ItemRef.name || 'N/A'}</strong>${description}</td>
        <td class="text-center">${item.SalesItemLineDetail.Qty || 1}</td>
        <td class="text-right">$${(item.SalesItemLineDetail.UnitPrice || 0).toFixed(2)}</td>
        <td class="text-right">$${(item.Amount || 0).toFixed(2)}</td>
      </tr>`;
  }
}

// Step 6: THE FIX - Conditionally create the PAID stamp HTML
let paidStampHtml = '';
// Only if the balance is zero or less, create the entire div. Otherwise, it's an empty string.
if (invoiceData.Balance <= 0) {
  paidStampHtml = '<div class="paid-stamp">PAID</div>';
}

// Step 7: Return the final JSON object
return [{
  json: {
    customerName: invoiceData.CustomerRef.name,
    customerEmail: invoiceData.BillEmail.Address,
    invoiceNumber: invoiceData.DocNumber,
    invoiceDate: formatDate(invoiceData.TxnDate),
    dueDate: formatDate(invoiceData.DueDate),
    lineItems: lineItemsHtml,
    subtotal: (invoiceData.TotalAmt - invoiceData.TxnTaxDetail.TotalTax).toFixed(2),
    tax: (invoiceData.TxnTaxDetail.TotalTax).toFixed(2),
    balanceDue: (invoiceData.Balance).toFixed(2),
    notes: invoiceData.CustomerMemo.value,
    paymentTerms: invoiceData.SalesTermRef.name,
    logoSrc: logoSrc,
    signatureSrc: signatureSrc,
    paidStampHtml: paidStampHtml // Pass the conditional HTML to the template
  }
}];

