// AI Response Parser for Prompt Refiner Workflow
// This script parses AI agent output and extracts structured data

const outputItems = []; // Array to store our final, precisely structured items

for (const item of items) {
    const inputItem = item.json; // The combined item from the Merge node

    // Initialize parsed data with defaults - MOVED OUTSIDE TRY BLOCK
    let parsedData = {
        "Original Prompt ID": $getWorkflowStaticData("global")["Original Prompt ID"] || null,
        "Prompt Title": "",
        "Topic": "",
        "Topic Categories": "",
        "Modified Prompt": "ERROR: Could not parse AI response",
        "Prompt Type": "Error",
        "Last Updated": new Date().toISOString(),
        "Improvement Notes": "",
        "Raw AI Output Length": 0,
        "Parsing Success": false,
        "Operation Counts": $getWorkflowStaticData("global").opm_counts || 0
    };

    try {
        // Get the AI output from the response
        let aiOutput = inputItem.output || inputItem.text || '';
      // console.log(aiOutput)
        parsedData["Raw AI Output Length"] = aiOutput.length;

        // Try different methods to extract JSON from AI response
        let jsonData = null;

        // Method 1: Look for JSON block between ```json and ```
        const jsonBlockMatch = aiOutput.match(/```json\s*([\s\S]*?)\s*```/);
        if (jsonBlockMatch) {
            try {
                jsonData = JSON.parse(jsonBlockMatch[1].trim());
            } catch (e) {
                console.log('Failed to parse JSON block:', e.message);
            }
        }

        // Method 2: Look for JSON object starting with { and ending with }
        if (!jsonData) {
            const jsonObjectMatch = aiOutput.match(/\{[\s\S]*\}/);
            if (jsonObjectMatch) {
                try {
                    jsonData = JSON.parse(jsonObjectMatch[0]);
                } catch (e) {
                    console.log('Failed to parse JSON object:', e.message);
                }
            }
        }

        // Method 3: Try to parse the entire output as JSON
        if (!jsonData) {
            try {
                jsonData = JSON.parse(aiOutput.trim());
            } catch (e) {
                console.log('Failed to parse entire output as JSON:', e.message);
            }
        }

        // If we successfully parsed JSON, extract the data
        if (jsonData) {
          parsedData["sessionId"] = $('PrepareForFirstGen - OPM').first().json.sessionId
          parsedData["Modified Prompt"] = jsonData.modified_prompt || jsonData.modifiedPrompt || "ERROR: No modified prompt found";
          parsedData["Prompt Type"] = jsonData.prompt_type || jsonData.promptType || "";
          parsedData["Prompt Title"] = jsonData.prompt_title || jsonData.promptTitle || "";
          parsedData["Topic"] = jsonData.topic || jsonData["Topic"] || "";
          parsedData["Topic Categories"] = jsonData.topic_categories || jsonData.topicCategory || "";
          parsedData["Parsing Success"] = true;

            // Extract additional fields
            parsedData["Improvement Notes"] = jsonData.improvement_notes || jsonData.improvementNotes || null;

            // If we have a valid modified prompt, change type from Error
            if (parsedData["Modified Prompt"] !== "ERROR: No modified prompt found") {
                // Keep the prompt type from JSON, but default to "Text" if it's still "Error"
                if (parsedData["Prompt Type"] === "Error") {
                    parsedData["Prompt Type"] = "Text";
                }
            } else {
                parsedData["Prompt Type"] = "Error";
            }
        } else {
            // If no JSON found, try to extract some basic info from text
            parsedData["Modified Prompt"] = "ERROR: AI output format unexpected (no JSON block).";
            parsedData["Improvement Notes"] = "AI did not respond in expected JSON format. Raw output length: " + aiOutput.length + " characters";
        }


    } catch (error) {
        // Handle any unexpected errors - parsedData is already defined above
        parsedData["Modified Prompt"] = `ERROR: Parsing failed - ${error.message}`;
        parsedData["Prompt Type"] = "Error";
        parsedData["Improvement Notes"] = `Parsing error: ${error.message}`;
        parsedData["Parsing Success"] = false;
    }

    outputItems.push(parsedData);
}

return outputItems;

// Fixed ExecutionController for n8n - Prevents Duplicate AI Executions
// This code prevents duplicate AI agent executions by tracking processed prompts
// Fixing loop, proccess twice at the same time, and limiting the number of executions for a single prompt

// Get input data
const input = $input.first();

// Check if input is existing and there is json in input
if (!input || !input.json) {
  throw new Error("No input data for AI Agent");
}
console.log("From OPG - Execution Controller: \n", input);

//-------------Global---Variables---Init--------------//
// Prevent multiple executions
// Initialize Execution Key use to track if the prompt has already executed.
const staticData = $getWorkflowStaticData("global");
const promptId = input.json["Original Prompt ID"];
const timestamp = new Date().toISOString();
const agentKey = `exec_prompt_#${promptId}_${timestamp}`;
const EXEC_KEY = "lastAgentExecutionKey";
const MAX_OPM_COUNTS = staticData.MAX_OPM_COUNTS || 3;
let systemMessage = "";
let opm_counts = staticData.opm_counts || 1;

// 1) Duplicate prevention
// Check if agent processes the input twice at the time.
if (staticData[EXEC_KEY] === agentKey) {
  console.log("Skipping duplicate AI execution");
  return [input];
}

staticData[EXEC_KEY] = agentKey;

// 2) Track how many times we've run for this prompt
if (staticData["Original Prompt ID"] === promptId) {
  opm_counts += 1;
} else {
  staticData["Original Prompt ID"] = promptId;
  opm_counts = 1;
}

staticData.opm_counts = opm_counts;

// check if agent processes the same prompt and if it not exeeds opm limits ,meaning: Do not let the OPM Agent run more than 3 times for 1 prompt.

// 4) Now check against your limit
if (opm_counts > MAX_OPM_COUNTS) {
  throw new Error("Exceeded Original Prompt Modifier Limits!");
}
const buildSystemMessage = () => {
  let message = ""
  // Check if from isMissingFields Node, if yes means regenerate with all missing data.
  if (opm_counts > 1) {
    message = `
