// Get the input data
const inputData = $input.all()[0].json; // Get the first item which contains the meeting data

// Extract segments from the nested structure
const segments = inputData.segments || [];

if (!segments || segments.length === 0) {
  return [{
    json: {
      fullTranscript: "No transcript data available",
      participants: "None",
      durationSeconds: 0,
      durationMinutes: 0
    }
  }];
}

// Combine into a single readable transcript
const transcript = segments
  .map(segment => {
    const speaker = segment.speaker || 'Unknown';
    const text = segment.text || '';
    const time = Math.round(segment.start || 0);
    return `[${time}s] ${speaker}: ${text}`;
  })
  .join('\n');

// Get unique participants
const participants = [...new Set(
  segments
    .map(segment => segment.speaker)
    .filter(speaker => speaker && speaker !== null)
)];

// Calculate duration
const startTime = segments[0]?.start || 0;
const endTime = segments[segments.length - 1]?.end || 0;
const duration = Math.round(endTime - startTime);

// Get meeting info from the root level
const meetingId = inputData.native_meeting_id || 'Unknown';
const platform = inputData.platform || 'Unknown';
const status = inputData.status || 'Unknown';

return [{
  json: {
    fullTranscript: transcript,
    participants: participants.join(', ') || 'Unknown',
    durationSeconds: duration,
    durationMinutes: Math.round(duration / 60),
    meetingId: meetingId,
    platform: platform,
    status: status,
    totalSegments: segments.length
  }
}];
