// Date and Location Metadata
const date = $('Schedule Trigger').item.json.timestamp.split('T')[0];
const location_id = $json.orders[0].location_id || null;
const location_name = $('Get Square Locations').item.json.locations.find(locations => locations.id === location_id)?.name;

// Our Result Variables
let total_money = 0;
let total_tax = 0;
let total_discount = 0;
let total_tip = 0;
let total_returns = 0;
let cash_rounding = 0;

let cash_tender = 0;
let card_tender = 0;
let gift_card_tender = 0;
let other_tender = 0;
let fees = 0;

// Loop Through Each Order
for (const sale of $json.orders) {

    // Add the sales, taxes, discounts and tips
    total_money += sale.total_money?.amount || 0;
    total_tax += sale.total_tax_money?.amount || 0;
    total_discount += -(sale.total_discount_money?.amount || 0);
    total_tip += sale.total_tip_money?.amount || 0;
    if (sale.rounding_adjustment) {
      cash_rounding += sale.rounding_adjustment.amount_money?.amount || 0;
    }


    if (sale.return_amounts) {
      // If there are returns, subtract from sales totals and add to return amount total
      total_money -= sale.return_amounts?.total_money?.amount || 0;
      total_tax -= sale.return_amounts?.tax_money?.amount || 0;
      total_discount -= sale.return_amounts?.discount_money?.amount || 0;
      total_tip -= sale.return_amounts?.tip_money?.amount || 0;

      total_returns += -(sale.return_amounts?.total_money?.amount || 0);
      total_returns -= -(sale.return_amounts?.tax_money?.amount || 0);
      total_returns -= -(sale.return_amounts?.tip_money?.amount || 0);
      total_returns -= -(sale.return_amounts?.discount_money?.amount || 0);

      // If an array of refunds is provided
      for (const refund of sale.refunds || []) {
        const transaction_id = refund.transaction_id;

        // Look for the original sale this refund refers to
        const original_sale = $json.orders.find(original =>
          original.id && transaction_id && original.id.includes(transaction_id)
        );

        if (original_sale) {
          if (original_sale.rounding_adjustment) {
            const amount = original_sale.rounding_adjustment.amount_money?.amount || 0;
            cash_rounding -= amount;
            total_returns += amount;
          }

          if (original_sale.tenders) {
            for (const tender of original_sale.tenders) {
              if (tender.id === refund.tender_id) {
                const amount = refund.amount_money?.amount || 0;
                if (tender.type === 'CARD') card_tender -= amount;
                else if (tender.type === 'CASH') cash_tender -= amount;
                else if (tender.type === 'SQUARE_GIFT_CARD') gift_card_tender -= amount;
                else other_tender -= amount;

                if (refund.processing_fee_money && tender.id === refund.tender_id) {
                  fees -= refund.processing_fee_money.amount || 0;
                }
              }
            }
          }
        }
      }
    }

    if (sale.tenders) {
      for (const tender of sale.tenders) {
        const amount = tender.amount_money?.amount || 0;
        if (tender.type === 'CARD') card_tender += amount;
        else if (tender.type === 'CASH') cash_tender += amount;
        else if (tender.type === 'SQUARE_GIFT_CARD') gift_card_tender += amount;
        else other_tender += amount;

        if (tender.processing_fee_money) {
          fees -= tender.processing_fee_money.amount || 0;
        }
      }
    }

}

// Final computed values
const net_sales = total_money - total_tip - total_tax - cash_rounding;
const gross_sales = net_sales - total_discount - total_returns;
const net_total = cash_tender + card_tender + gift_card_tender + other_tender + fees;

return {
  json: {
    date,
    location_id,
    location_name,
    gross_sales: gross_sales / 100.0,
    total_returns: total_returns / 100.0,
    total_discount: total_discount / 100.0,
    net_sales: net_sales / 100.0,
    total_tax: total_tax / 100.0,
    total_tip: total_tip / 100.0,
    cash_rounding: cash_rounding / 100.0,
    total_payments_collected: total_money / 100.0,
    cash: cash_tender / 100.0,
    card: card_tender / 100.0,
    gift_card: gift_card_tender / 100.0,
    other: other_tender / 100.0,
    fees: fees / 100.0,
    net_total: net_total / 100.0,
  }
};
