// Function to extract supplier category from name
function extractSupplierCategory(supplierName) {
  const name = supplierName.toLowerCase();

  if (name.includes('tech') || name.includes('software') || name.includes('data') || name.includes('digital')) {
    return 'Technology';
  } else if (name.includes('manufacturing') || name.includes('production') || name.includes('factory')) {
    return 'Manufacturing';
  } else if (name.includes('energy') || name.includes('power') || name.includes('green') || name.includes('renewable')) {
    return 'Energy';
  } else if (name.includes('logistics') || name.includes('transport') || name.includes('delivery') || name.includes('shipping')) {
    return 'Logistics';
  } else if (name.includes('security') || name.includes('cyber') || name.includes('safe')) {
    return 'Cybersecurity';
  } else {
    return 'Technology'; // Default fallback
  }
}

